-- ----------------------------
-- Table structure for project_policy_info
-- ----------------------------
DROP TABLE IF EXISTS `project_policy_info`;
CREATE TABLE `project_policy_info` (
`id`  bigint(20) NOT NULL AUTO_INCREMENT ,
`project_policy_id`  bigint(20) NULL DEFAULT NULL ,
`id_phase`  bigint(20) NULL DEFAULT NULL ,
`year`  bigint(5) NULL DEFAULT NULL ,
`title`  text CHARACTER SET utf8 COLLATE utf8_general_ci NULL ,
`description`  text CHARACTER SET utf8 COLLATE utf8_general_ci NULL ,
`other`  text CHARACTER SET utf8 COLLATE utf8_general_ci NULL ,
`rep_ind_policy_investment_type_id`  bigint(20) NULL DEFAULT NULL ,
`amount`  double(30,2) NULL DEFAULT NULL ,
`rep_ind_organization_type_id`  bigint(20) NULL DEFAULT NULL ,
`rep_ind_stage_process_id`  bigint(20) NULL DEFAULT NULL ,
`project_expected_study_id`  bigint(20) NULL DEFAULT NULL ,
`rep_ind_geographic_scope_id`  bigint(20) NULL DEFAULT NULL ,
`has_milestones`  tinyint(1) NULL DEFAULT NULL ,
`narrative_evidence`  text CHARACTER SET utf8 COLLATE utf8_general_ci NULL ,
`external_link`  text CHARACTER SET utf8 COLLATE utf8_general_ci NULL ,
`global_unit_id`  bigint(20) NULL DEFAULT NULL ,
PRIMARY KEY (`id`),
FOREIGN KEY (`project_policy_id`) REFERENCES `project_policies` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
FOREIGN KEY (`id_phase`) REFERENCES `phases` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
FOREIGN KEY (`rep_ind_geographic_scope_id`) REFERENCES `rep_ind_geographic_scopes` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
FOREIGN KEY (`global_unit_id`) REFERENCES `global_units` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
INDEX `project_policy_id` (`project_policy_id`) USING BTREE ,
INDEX `id_phase` (`id_phase`) USING BTREE ,
INDEX `rep_ind_policy_investment_type_id` (`rep_ind_policy_investment_type_id`) USING BTREE ,
INDEX `rep_ind_organization_type_id` (`rep_ind_organization_type_id`) USING BTREE ,
INDEX `rep_ind_stage_process_id` (`rep_ind_stage_process_id`) USING BTREE ,
INDEX `project_expected_study_id` (`project_expected_study_id`) USING BTREE ,
INDEX `rep_ind_geographic_scope_id` (`rep_ind_geographic_scope_id`) USING BTREE ,
INDEX `project_policy_info_ibfk_8` (`global_unit_id`) USING BTREE 
)
ENGINE=InnoDB
DEFAULT CHARACTER SET=utf8 COLLATE=utf8_general_ci
AUTO_INCREMENT=6341

