INSERT INTO `project_policy_info` VALUES ('1', '1', '11', '2017', 'Informed investment of  USD 1.2 million for scaling up `Underground Taming of Floods for Irrigation` (UTFI) in India', null, null, null, null, null, null, '2127', '4', null, null, null, '1'), ('12', '2', '11', '2017', 'Informed investments of USD 170 million in India for scaling up the Happy Seeder technology', null, null, null, null, null, null, '2039', '4', null, null, null, '1'), ('23', '3', '11', '2017', 'Process of developing a new international system of plant germplasm exchange, as part of implementation of the International Treaty on Plant Genetic Resources for Food and Agriculture ITPGRFA.', null, null, null, null, null, null, '2121', '1', null, null, null, '1'), ('34', '4', '11', '2017', 'At the national level, Bhutan, Burkina Faso, Costa Rica, Côte d’Ivoire, Guatemala, Nepal, Rwanda and Uganda adopted national policies and laws for implementing the ITPGRFA.', null, null, null, null, null, null, '2119', '3', null, null, null, '1'), ('45', '5', '11', '2017', ' In Madagascar and Benin, new laws were passed to enhance capacity to adapt to climate change by implementing the ITPGRFA and the Nagoya protocol.  [If this needs to be broken down further, please create a row for each - else report this as multicountry, 2 policies]', null, null, null, null, null, null, '1952', '3', null, null, null, '1'), ('56', '6', '11', '2017', 'Informed investment of USD 1.6 million for Climate Smart Agriculture (CSA) in Niger', null, null, null, null, null, null, '2055', '4', null, null, null, '1'), ('67', '7', '11', '2017', 'Informed investment of  USD 21.5 billion by the Indian Government to provide 2.75 million solar pumps to farmers and farmer cooperatives.  ', null, null, null, null, null, null, '2103', '4', null, null, null, '1'), ('78', '8', '11', '2017', 'Informed investment of  USD 2 million for a regional Climate Smart Agriculture (CSA) strategy for Central America', null, null, null, null, null, null, '2164', '3', null, null, null, '1'), ('89', '9', '11', '2017', 'Kenya launched the Climate Smart Agriculture Strategy 2017‐2026. ', null, null, null, null, null, null, '2122', '4', null, null, null, '1'), ('100', '10', '11', '2017', 'Informed investment of USD 66 million in Colombia for scaling up Climate Smart Agriculture (CSA) in Cauca, Colombia ', null, null, null, null, null, null, '2136', '4', null, null, null, '1'), ('111', '11', '11', '2017', ' Rwanda initiated a national policy framework for climate services under the UN Global Framework for Climate Services', null, null, null, null, null, null, '2098', '4', null, null, null, '1'), ('122', '12', '12', '2017', 'Contributions from the Nigeria Strategy Support Program to the Agricultural Sector Food Security and Nutrition Strategy (2016-2025) were acknowledged by the Federal Ministry of Agriculture and Rural Development.', null, null, null, null, null, null, '2190', '4', null, null, null, '3'), ('133', '13', '12', '2017', 'ASTI data contributed to a policy that prioritized higher education training for agricultural researchers in Swaziland, and was used in presentations, posters, and meeting communications to advocate for a reform of the national agricultural research system.', null, null, null, null, null, null, '2095', '4', null, null, null, '3'), ('144', '14', '12', '2017', 'ASTI data contributed to justify the need for the merger of several institutions to form the Kenya Agriculture and Livestock Research Organization (KALRO), for improved capacity and performance.  ', null, null, null, null, null, null, '2096', '4', null, null, null, '3'), ('155', '15', '12', '2017', 'The Collaborating for Resilience approach was used by International Land Coalition’s National Engagement Strategy (NES).', null, null, null, null, null, null, '2203', '3', null, null, null, '3'), ('166', '16', '12', '2017', 'The African Land Policy Centre approved the Monitoring and Evaluation for Land in Africa (MELA) framework for dissemination in 12 African Countries.', null, null, null, null, null, null, '2015', '3', null, null, null, '3'), ('177', '17', '12', '2017', 'Joint Village Land Use Planning methodology was used by the government to increase tenure for pastoralists in Tanzania.', null, null, null, null, null, null, '2033', '4', null, null, null, '3'), ('180', '17', '57', '2018', 'Tanzania\'s Land Use Policy 2007 and Village Land Act 1999   ', null, '', '1', '0.00', '8', '4', null, null, null, 'Joint Village Land Use Planning (JVLUP) contributes to the implementation of Tanzania\'s Land Use Policy 2007, the Village Land Act 1999, the National Land Use Planning Commission (NLUPC) Guidelines on Participatory Land Use Planning, the Tanzania National Land Use Framework 2013-33, and the NLUPC Manual on Tools and Spatial Technologies for Village Land Use Planning. JVLUP is a process of local (village) level land use planning that works across village boundaries to facilitate the governance and management of shared resources such as grazing.  ', null, '3'), ('188', '18', '12', '2017', 'GIZ used research results to include tenure into future land restoration programs.', null, null, null, null, null, null, '2036', '3', null, null, null, '3'), ('199', '19', '12', '2017', 'Investment by Brazil and Ghana in the new phase of Agricultural Mechanization Services Enterprise Centers in Ghana were based on recommendations from PIM’s research on mechanization. ', null, null, null, null, null, null, '2189', '3', null, null, null, '3'), ('205', '19', '102', '2019', 'Agricultural Mechanisation Services Enterprise Centers program in Ghana', 'Recommendations from PIM were adopted to make Ghana\'s Agricultural Mechanization Service Enterprise Centers more efficient and inclusive.', '', '1', '0.00', null, '4', null, null, '1', '', null, '3'), ('210', '20', '12', '2017', 'AGRA used the results of PIM’s research on agricultural transformation to inform its strategy.', null, null, null, null, null, null, '2193', '1', null, null, null, '3'), ('221', '21', '12', '2017', 'IFAD used the Rural Investment and Policy Analysis (RIAPA) model to help inform investment priorities.', null, null, null, null, null, null, '2193', '1', null, null, null, '3'), ('232', '22', '12', '2017', 'PIM’s research on structural transformation contributed to the 2017 DFID Economic Development Strategy.  ', null, null, null, null, null, null, '2193', '1', null, null, null, '3'), ('243', '23', '12', '2017', 'SPEED data was used to inform World Bank loans and government investments in Burkina Faso, Malawi, Mali, Nigeria, Togo, and Zambia. ', null, null, null, null, null, null, '2192', '3', null, null, null, '3'), ('254', '24', '12', '2017', 'The Nepal Ministry of Agricultural Development took up suggestions on food technology and quality control and the structure of agricultural training centers. ', null, null, null, null, null, null, '2192', '3', null, null, null, '3'), ('265', '25', '12', '2017', 'The African Union’s Comprehensive Africa Agriculture Development Programme (CAADP) biennial reporting framework and guidelines in 2017 recommended the use of the WEAI. ', null, null, null, null, null, null, '2008', '1', null, null, null, '3'), ('276', '26', '12', '2017', 'Egypt’s Ministry of Social Solidarity (MOSS) used results of the impact evaluation of the Takaful and Karama program to inform changes in the eligibility criteria for the program and the decision to add messages promoting women’s empowerment. ', null, null, null, null, null, null, '2171', '4', null, null, null, '3'), ('279', '26', '57', '2018', 'Egypt\'s Takaful and Karama Cash Transfer Program (improved targeting criteria)', null, '', '1', '0.00', null, '3', null, null, null, 'Lessons from the impact evaluation of Egypt’s cash transfer \"Takaful and Karama\" program conducted by PIM contributed to the decision of the Government of Egypt to continue to fund this program and to the World Bank\'s decision to continue to support it. The Government of Egypt adopted several recommendations from the evaluation, including on how to improve targeting by increasing awareness about the program among the very poor. ', null, '3'), ('287', '27', '12', '2017', 'Research on Ethiopia’s health insurance and safety net programs informed the decision by the government to closely integrate two programs (PNSP and CBHI).', null, null, null, null, null, null, '2198', '4', null, null, null, '3'), ('298', '28', '12', '2017', 'Based on PIM’s research, BKC WeatherSys augmented their advisory application with a feature allowing farmers to take smartphone pictures to collect additional training data for picture-based advisory/insurance services. ', null, null, null, null, null, null, '2173', '4', null, null, null, '3'), ('309', '29', '12', '2017', 'HDFC, an Indian insurance company, is investing staff time and resources towards developing and testing picture-based insurance products as part of the partnership with IFPRI. ', null, null, null, null, null, null, '2173', '4', null, null, null, '3'), ('320', '30', '12', '2017', 'PIM research was used extensively in an EU report evaluating different effects of alternative agreements to support trade negotiations between the European Union and two African Regional Economic Communities.  ', null, null, null, null, null, null, '2199', '2', null, null, null, '3'), ('331', '31', '12', '2017', 'The Ag-incentive consortium (FAO, IFPRI, Inter-American Development Bank, OECD, World Bank) implemented the strategy to harmonize data on agricultural distortions and publish these data.  ', null, null, null, null, null, null, '2201', '1', null, null, null, '3'), ('512', '32', '59', '2018', 'testing - GEE', null, '', null, '0.00', null, null, null, null, null, null, null, '5'), ('629', '41', '56', '2018', 'Local Technical Agroclimatic Committees as an implementing tool of the Regional Strategy for Disaster Risk Management in the Agriculture Sector and Food and Nutrition Security in Latin America and the Caribbean (2018–2030)', null, '', '1', '0.00', '10', '3', null, null, null, 'CCAFS\' Local Technical Agroclimatic Committees (LTAC) approach was selected as a prioritized area for implementation of the Regional Strategy for Disaster Risk-Management in the Agriculture-Sector and Food and Nutrition Security in LatinAmerica. Two major successes have been achieved, Chile adds to the group of Latin American countries to adopt the LTAC approach (6 total), and Peru, Bolivia, Paraguay, and Ecuador are designing their plans to implement the approach according to their context-specific needs and capacities supported by FAO-CCAFS collaborative work.', null, '1'), ('642', '42', '56', '2018', 'Mbale District (Uganda) counterfeit agricultural inputs (prohibition) ordinance, 2018. Passed to stem the influx of fake agricultural inputs which was discouraging farmer use and reducing trust in the market.', null, '', '1', '0.00', '4', '4', null, null, null, '', null, '1'), ('655', '43', '56', '2018', 'Implementing the resolution of 9th Congress of the Farmers Association of Ha Tinh province, Vietnam that integrates climate-smart agriculture as part of the 2018–2023 provincial strategy', null, '', '1', '0.00', '8', '3', null, null, null, 'Farmers\' Association (aka Farmers\' Union) has been an active implementing partner throughout the CCAFS-projects in My Loi CSV, the province level has been targeted in various training and promotion and exchange events. The advocacy and research evidence on CSA practices, and the participatory approaches, like farmers interest groups, are  integrated in Farmers\' Associations\' new five-year strategy 2018-2023. About 180.000 members in the province can potentially benefit from CSA.  ', null, '1'), ('707', '47', '56', '2018', 'Implementation guidelines approved in Benin and Madagascar to operationalize laws adopted in 2017 governing access and benefit sharing of plant genetic resources. National partners in the two countries developed guidelines for the day-to-day operation of these laws, including details about processes, decision making, relationships between agencies with different, but related, responsibilities under the laws', null, '', '1', '0.00', '8', '4', null, null, null, '', null, '1'), ('720', '48', '56', '2018', 'Two community biocultural protocols aimed at promoting farmers as managers, providers and recipients of genetic materials and developing expertise for climate change adaptation approved by municipal governments in Benin', null, '', '1', '0.00', '8', '4', null, null, null, '', null, '1'), ('733', '49', '56', '2018', 'Kenya Climate Smart Agriculture Implementation Framework (2018 - 2027)', null, '', '1', '0.00', '8', '4', null, null, null, '', null, '1'), ('746', '50', '56', '2018', 'National strategy for conservation of plant genetic resources in South Africa', null, '', '1', '0.00', '8', '4', null, null, null, '', null, '1'), ('759', '51', '56', '2018', 'National policies regarding the establishment and support of community seedbanks in Nepal', null, '', '1', '0.00', '4', '3', null, null, null, 'The 2nd National Workshop produced a series of policy recommendations for the establishment and support of community seedbanks in Nepal, which were presented to key national authorities during the workshop. National authorities recognized the importance of the recommendations and agreed to discuss them in the context of the revision of national agricultural policies relevant to community seedbanks. The national authorities invited workshop participants to continue the process of engagement. See:\r\nhttps://cgspace.cgiar.org/handle/10568/99141', null, '1'), ('772', '52', '63', '2018', 'Quantify effects of dietary change on the future demand for major cereals - case study in Africa', null, '', '1', '0.00', '1', '3', null, null, null, '', null, '22'), ('798', '54', '56', '2018', 'National Council on Food Security (CONASAN) approves a community-based food security monitoring and early warning system developed using CCAFS science', null, '', '1', '0.00', '8', '4', null, null, null, 'See Project Evidence OICR No. 151. In 2018, the Guatemalan Secretariat for Food and Nutrition Security (SESAN) adopted an information system that supports the local food security monitoring and early warning in all 340 municipalities of the country. The adoption of the system responds to SESAN\'s legal obligation to design and implement a local food security monitoring and early warning system. CCAFS scientists guided and supported the iterative design process through particiaptory studies on users’ needs, information preferences and their decision-making context. Results influenced design of the information system (e.g. selection of indicator, estimation of a local food insecurity index, definition of protocols and information flow within the system etc.). The secretary of SESAN acknowledged the important contribution Bioversity and CCAFS AgroClimas provided during the process. The information system is used by SESAN technical field staff. Data collection started at the end of 2018. The information supports decisions in terms of effectively targeting communities at risk, allocating scarce resources, increasing coordination and thus shifting from reactive crisis response to proactive food security management. This will benefit 1.5m people regularly at risk of drought-related acute food insecurity in Guatemala as their needs will be identified and attended earlier and more effectively.', null, '1'), ('811', '55', '56', '2018', 'Green Growth policy of Colombia (National Council of Economic and Social Policy (CONPES) 3934)', null, '', '1', '0.00', '8', '4', null, null, null, '', null, '1'), ('928', '64', '56', '2018', 'Guideline for mainstreaming climate change adaptation and mitigation in national and sub-national agricultural sector policies and plans in Uganda', null, '', '1', '0.00', '4', '4', null, null, null, '', null, '1'), ('954', '66', '65', '2018', 'Research Data Management Policy', null, 'This is an internal policy among ICRAF scientists and our partners.', '1', '0.00', '11', '3', null, null, null, '', null, '24'), ('993', '69', '56', '2018', 'G7 Development Ministers\' Meeting Resolution on Empowering Women and Girls', null, '', '1', '0.00', '10', '3', null, null, null, 'CCAFS provided inputs to a submission to the G7 Development Ministers by the G20 Gender and Growth Initiative at a roundtable in Ottawa on May 3. The contribution from CCAFS that \"Women should be considered as economic actors, especially in the tech sector\" was the only input that called for the active the role of women and girls in technology and innovation.  The news release issued from the meeting of the G7 Development Ministers on June 2 noted four resolutions were adopted by the G7 ministers, included similar text: \"engaging women and girls as both recipients and resources of innovation\" in the resolution on accelerating innovation for development impact: accelerating innovation for development impact by encouraging new ways of working, investing in locally driven solutions, using evidence to drive decision making, taking intelligent risks, and by promoting inclusivity and engaging women and girls as both recipients and resources of innovation\".', null, '1'), ('1006', '70', '56', '2018', 'Ethiopian Climate Resilient Green Economy Strategy: Sector-wise Second Growth and Transformation Plan (GTP II) Implementation Monitoring Checklist for Government Ministries', null, '', '1', '0.00', '8', '3', null, null, null, 'Bayleyegn D, Ericksen P, Solomon D. 2018. Climate Resilient Green Economy Strategy: https://hdl.handle.net/10568/97788 \r\n\r\nA detailed sector-wise checklist of reduction activities, set out by responsible line ministries during the GTP II period (2016-2020), towards contributing their share in building resilience and reducing GHG emissions.\r\n', null, '1'), ('1045', '73', '56', '2018', 'Investment by two Nepalese states in the \"Chief Minister\'s Climate Smart Agriculture Village Model Program\"', null, '', '1', '5000000.00', '8', '4', null, null, null, '', null, '1'), ('1048', '73', '101', '2019', 'Investment by two Nepalese states in the \"Chief Minister\'s Climate Smart Agriculture Village Model Program\"', 'Two state governments in Nepal (State 5 and Gandaki) have so far made an allocation of 700+ million NPR investments for scaling climate-smart villages', '', '1', '5000000.00', '8', '5', null, null, '0', '', null, '1'), ('1063', '74', '56', '2018', 'Supporting Directives and Circulars of the Ministry of Agriculture and Rural Development of Vietnam (MARD) and the Department Crop Production for implementation of Climate-Related Risk Maps and Adaptation Plans (CS-MAP) particularly the adjustment of planting dates', null, '', '1', '0.00', '8', '4', null, null, null, 'On 22 February 2017, the DCP issued the official Circular number 184/TT-CLT, requesting the MRD provinces to integrate the identified adaptive measures into the provincial action plan(http://www.cuctrongtrot.gov.vn/TinTuc/Index/3607). \r\n\r\nMARD Decision QD-BNN-KH 1915 issued on May 28, 2018, further supported the upscaling of CS-MAP(http://vukehoach.mard.gov.vn/DataStore/News/20186423-De%20an%20pt%20sx%20lua%20vung%20DBSCL%20trong%20dk%20BDKH.PDF.\r\n\r\nDuring planning meeting last August 2018, the MARD Vice Minister directed the DCP to monitor the upcoming El Nino and apply the CS-MAP for possible adjustments in the rice planting calendar during the Winter-Spring season(documented in Announcement no. 6194/TB-BNN-VP). In this Announcement, Vice Minister Le Quoc Doanh instructed DCP to coordinate with provincial DARDs to implement the CS-MAP. The budget for CS-MAP implementation can be considered from VnSAT project(https://www.dropbox.com/s/14ofqi6c4qb1ifs/Announcement%20no.%206194-TB-BNN-VP%20on%20the%20Conclusion%20remark%20of%20Vice%20Minister%20Le%20Quoc%20Doanh.pdf?dl=0)\r\n\r\nDCP  Directive no. 1501/TT-VPPN on prompt implementation of the rice production plan in South Eastern Region and Mekong River Delta which directed the Provincial Division of Crop Production in 13 MRD provinces to shift the rice planting date in preparation for El Nino.   In this Directive, DCP based on the Announcement no. 6194 to request provincial DARD of 13 MRD provinces to review and adjust the rice planting calendar of Winter-Spring season 2018-2019 accordingly in order to mitigate the impacts of the upcoming El Nino in early 2019(https://www.dropbox.com/s/98k5wg58dg45it2/Directive%20no.%201501-TT-VPPN%20on%20prompt%20implementation%20of%20the%20production%20plan%20in%20South%20Eastern%20Region%20and%20Mekong%20River%20Delta.pdf?dl=0) .\r\n', null, '1'), ('1066', '74', '101', '2019', 'Supporting directives and circulars of the Ministry of Agriculture and Rural Development of Vietnam(MARD) and the Department of Crop Production for implementation of CS-MAP, particularly the adjustment of planting dates', 'The directives supported the implementation CS MAP in the MRD.', '', '1', '0.00', '8', '5', null, null, '0', '', null, '1'), ('1076', '75', '56', '2018', 'World Bank agricultural investments for improved climate change resilience in the ag sector and reduced contributions to GHG emissions rise from 28% (2016) to 45% (2018) of committed budgets of new agriculture projects ', null, '', '3', '500000000.00', '10', '4', null, null, null, '', null, '1'), ('1089', '76', '56', '2018', 'Integration of Climate Smart Agriculture(CSA) competencies by the Philippine Department of Education in the curricula of 75 schools nationwide that now serve as CSA information hubs among the 278 technical and volcational (TechVoc) secondary schools.', null, '', '4', '0.00', '8', '4', null, null, null, 'Memorandum dated February 27, 2015 from the Undersecretary of the Philippines Department of Education to Technical Vocational High School (TechVoc) emphasizing the importance of the Infomediary Campaign,  identifying the 97 selected participants in the Infomediary in 2015,  training and instructing the participating teachers to integrate the CSA competencies in the Crop Production Course for the school year 2015-16. The Memorandum was directed to all TechVoc High School all over the Philippines.\r\n\r\nThis was followed by other memoranda (e.g. DM-42018-00377 dated October 3, 2018) emphasizing the importance of integrating CSA in crop production courses in high schools in bringing back the interest of students on agriculture.  Teaching guide and modules have also been developed (http://www.infomediary4d.com/resources/teaching-materials/).\r\n', null, '1'), ('1102', '77', '56', '2018', 'Extension policy of Olam Uganda -- Olam Uganda, a private company working with coffee farmers, has incorporated \'Stepwise\', an an approach to climate change, into its farmer training', null, '', '1', '0.00', '6', '3', null, null, null, 'The Stepwise methodology is not only influencing the way Olam staff provide technical advice to coffee smallholder in Uganda, but it’s also being used to guide technical support to smallholders in other regions and for other products (e.g. cocoa West Africa). ', null, '1'), ('1128', '79', '56', '2018', 'International Fund for Agricultural Development’s (IFAD) framework for implementing transformational approaches to the mainstreaming themes: climate and environment, gender, nutrition and youth', null, '', '1', '0.00', '7', '3', null, null, null, 'In 2018, CCAFS worked with IFAD to develop the Framework for implementing transformational approaches to the mainstreaming themes: climate and environment, gender, nutrition and youth. This framework endeavours to provide guidance on how the mainstreaming themes will be integrated to deliver transformational outcomes for IFAD’s target groups. It is a tool to support the conceptualisation of an integrated approach to mainstreaming themes and promote its implementation in IFAD. Specifically, the framework will enable IFAD to optimise synergies between the themes for more relevance, efficiencies and effectiveness. This is a key commitment of IFAD\'s eleventh replenishment and will guide investments and implementation in future years.\r\n\r\n', null, '1'), ('1167', '82', '56', '2018', 'Association of Southeast Asian Nations (ASEAN) guidelines for agroforestry development', null, '', '1', '0.00', '8', '3', null, null, null, 'The ASEAN Guidelines for Agroforestry Development is an important milestone towards increasing the prosperity, connectivity, resilience and security of the peoples of ASEAN Member States. Taken as a whole, the Guidelines form a framework in which development can take place. \r\nThe Guidelines are an outcome of the Vision and Strategic Plan for ASEAN Cooperation in Food, Agriculture and Forestry 2016?2025 that was endorsed by the ASEAN Ministers of Agriculture and Forestry in 2016. That endorsement led to a decision to develop guidelines for the development of agroforestry in ASEAN Member States, through an extensive consultation process across the region with stakeholders not only from agriculture and forestry but also other sectors, such as climate change, energy and water.\r\n\r\nProcess explained: http://blog.worldagroforestry.org/index.php/2019/01/16/asean-guidelines-for-agroforestry-development-set-to-revolutionize-land-use-in-southeast-asia/\r\nPolices now being taken to implementation in Myanmar and Cambodia.', null, '1'), ('1180', '83', '56', '2018', '\"20-20-20\" Decision No. 543/QD-BNN-KHCN (from 2011)', null, '', '1', '0.00', '9', '5', null, null, null, '', null, '1'), ('1193', '84', '56', '2018', 'Intended Nationally Determined Contribution of Viet Nam', null, '', '1', '0.00', '8', '4', null, null, null, '', null, '1'), ('1219', '86', '56', '2018', 'Changed operating protocols by a major insurance partner to support their most vulnerable customers', null, '', '1', '0.00', '6', '3', null, null, null, 'Our industry partner design and broker agricultural insurance to smallholders, working in eight countries across Africa and Asia. In 2017, they facilitated crop and livestock insurance cover to 611,000 farmers in Kenya, Rwanda, Uganda, Nigeria, Ethiopia and Malawi and are aiming to reach 1000000 farmers by 2020. Pula design the technical side of their products and indices, plus the logistics and social side of programmes, examining the impact that their work has had on farmers.  \r\nThe CCAFS project made several changes to their internal protocols and policies:\r\n - They attended a joint CCAFS workshop in 2016 on satellites and insurance, resulting in them switching some of their drought products from NOAA ARC2 to USCB CHIRPS, a more stable satellite with lower basis risk.\r\n - An early result from our Malawi fieldwork was that thousands of their customers linked with a major NGO were mis-registering the location of their products (registering the depot location rather than the field).  These are some of their most vulnerable farmers. As a result, in 2018/2019, the company changed practice to have specific staff to support registration for those farmers.\r\n - We\'ve provided meaningful support on impact survey design, including the impact on different demographics.', null, '1'), ('1232', '87', '56', '2018', 'Implementing the Resolution of the 9th Congress of the Farmers Association of Ha Tinh province through the 2018-2023 province strategy.', null, '', '1', '0.00', '8', '3', null, null, null, 'Farmers\' Association (aka Farmers\' Union) has been an active implementing partner throughout the CCAFS-projects in My Loi CSV, the province level has been targeted in various training and promotion and exchange events. The advocacy and research evidence on CSA practices, and the participatory approaches, like farmers interest groups, are  integrated in Farmers\' Associations\' new five-year strategy. The collaboration with ICRAF and CARE is stressed in the policy. ', null, '1'), ('1245', '88', '56', '2018', 'Comprehensive Development Plan(2016–2022) of the Municipality of Ivisan, Capiz Province, Philippines includes CSA program with uptake of CSA practices as success indicator of climate resilience. ', null, '', '1', '0.00', '8', '4', null, null, null, '', null, '1'), ('1258', '89', '56', '2018', 'CCAFS\' Climate Resilence and Vulnerability Assessment(CRVA) maps were used as referenced in developing the the National Color-Coded Agricultural Guide (NCCAG) in the Philippines.', null, '', '1', '0.00', '4', '4', null, null, null, 'CIAT’s CRVA maps were also referenced in the development of maps for another component the AMIA Program, the National Color-Coded Agricultural Guide (NCCAG). The NCCAG Map is a campaign commitment of Philippine President Duterte which enables farmers and fishermen to access interactive data on crops suitability, rainfall pattern, temperature, and climate-induced multi-hazards, among others.', null, '1'), ('1271', '90', '56', '2018', 'CCAFS and PIM science referenced in formulating the national and sectoral Philippine Medium Term Development Plan 2017–2022', null, '', '1', '0.00', '4', '4', null, null, null, 'CIAT science has been referenced in formulating national and sectoral midterm plans of the government of the Philippines. At the national level, CIAT climate science information, tools and methodologies were referenced in Chapter 20 of the Philippine Development Plan 2017-2022: Ensuring Ecological Integrity, Clean and Healthy Environment in formulating strategies for attaining increased adaptive capacities and resilience of ecosystems.', null, '1'), ('1284', '91', '56', '2018', 'Agriculture and Fisheries Modernization Plan 2018–2022  integrates CCAFS\' Climate Risk Vulnerability Assessment(CRVA) as one of its planning tools', null, '', '1', '0.00', null, '4', null, null, null, 'At the sectoral level, in the approved 2018-2022 Agriculture and Fisheries Modernization Plan, the DA has declared CRVA as one of its planning tools in assessing the vulnerability of areas to climate change.', null, '1'), ('1297', '92', '57', '2018', 'Mali\'s cash transfer program (Filets Sociaux Jigisemejiri) (improved design)', null, '', '1', '0.00', null, '4', null, null, null, 'In an email to the IFPRI Principal Investigator dated November 2018, Mahamoud Ali Sako (Government of Mali staff) acknowledges the use of the evaluation findings and recommendations in the design of the second phase of the program starting in 2019. The World Bank Implementation Status & Results Report mentions the IFPRI/PIM impact evaluation report as the rationale for modifying the design of the measures accompanying the cash transfers. ', null, '3'), ('1310', '93', '57', '2018', 'Bangladesh\'s Vulnerable Group Development program (improved targeting criteria)', null, '', '1', '0.00', null, '4', null, null, null, '', null, '3'), ('1328', '94', '56', '2018', 'Vision 2045 for Agriculture Sector in Bhutan - strategic planning to achieve food self sufficiency, livelihood security, and environmental conservation,   ', null, '', '1', '0.00', '8', '3', null, null, null, 'IFPRI South Asia Office has conducted a capacity building training for the government officials in Bhutan during 5th to 9th November. Purpose of this capacity building training was to sensitize the task force  formed by the department of agriculture, Govt. of Bhutan to prepare a vision 2045 document for agriculture sector. This is the first ever initiative by the Govt. of Bhutan to prepare a vision for agriculture sector.   ', null, '1'), ('1341', '95', '56', '2018', 'Mbale District (Uganda) coffee (management) bill, 2018', null, '', '2', '0.00', '4', '4', null, null, null, '', null, '1'), ('1354', '96', '56', '2018', 'Inputs on Land Productivity for the Long-Term Green Growth Policy of Colombia', null, '', '1', '0.00', '8', '4', null, null, null, 'The government of Colombia commissioned CCAFS and CIAT to develop technical and policy recommendations in the agricultural sector for the Green Growth Policy in Colombia. The study was focused on promising technologies in prioritized agricultural systems that could significantly improve the performance of the country with respect to the green growth international indicators. Technical and policy recommendations made by CCAFS and CIAT were considered in the final document of the policy.', null, '1'), ('1393', '99', '57', '2018', 'Woreda Participatory Land Use Planning (WPLUP) for Pastoral Areas ', null, '', '1', '0.00', '8', '3', null, null, null, '', null, '3'), ('1411', '100', '56', '2018', 'CCAFS and PIM science referenced in formulating the national and sectoral Philippine Medium Term Development Plan 2017–2022', null, '', '1', '0.00', '8', '4', null, null, null, '', null, '1'), ('1437', '102', '56', '2018', 'National extension training materials/ curriculum developed for cocoa in Ghana. These materials form the obligatory base for all public and private extension materials for cocoa in the country.', null, '', '4', '0.00', '8', '4', null, null, null, 'COCOBOD has incorporated findings from this project in the most recent version of its Cocoa Extension Manual in Ghana. In addition, training materials have been developed with Rainforest Alliance for use in country. All cocoa sector actors must use COCOBOD materials by law. ', null, '1'), ('1463', '104', '56', '2018', 'Implementing the resolution of 9th Congress of the Farmers Association of Ha Tinh province, Vietnam that integrates climate-smart agriculture as part of the 2018–2023 provincial strategy', null, '', '1', '0.00', '8', '4', null, null, null, 'These policies are  result of frequent policy dialogues and bottom-up process of scaling CSA and ACIS. Joint outcome on policy evidence of P48 P55 work in My Loi CSV Builds on 5 documents: \r\nI. Farmers Association district to province\r\n (1) Report from the Executive Committee of Ky Anh District Farmer Association submitted to the 9th Congress delegates for 2018 – 2023; sharing lessons learned from international cooperation in My Loi CSV. This led to the uptake of \r\n(2) Ha Tinh province Farmer Association Strategy 2018-2023 (submitted as separate policy), which stressed collaboration with INGOs ICRAF and CARE on CSA and climate adaptation.  \r\nII. Commune People\'s Committee incorporates CSA in New Rural Development Programme, investing in 12 ha CSA-home garden, nursery and CSA for main crops, e.g. rice, peanut. \r\n(3) Ky Son commune People’s committee, November 2018: results on socioeconomics and livelihoods 2018\r\n(4) Ky Son commune Agriculture production plan for 2019 (Ky Son People\'s Committee No 33-KH/UBND of 12 Dec 2018)\r\n(5) Ky Son commune Plan for spring crop 2019 (Ky Son People\'s Committee No 04-DA/UBND 25 Dec 2018) \r\nPolicies #4 & 5 is the written evidence of agriculture planning is informed by seasonal forecasts, rather than climate averages. ', null, '1'), ('1476', '105', '56', '2018', 'Local government of Guinayangan, Quezon Province, Philippines emphasized Climate Smart Agriculture in its 2017–2022 Comprehensive Development Plan & Municipal Agriculture Office Banner Programs, following the participatory development of Guinayangan Climate Smart Village.', null, '', '1', '0.00', '8', '4', null, null, null, '', null, '1'), ('1489', '106', '56', '2018', '2 Commune plans with climate informed agriculture planning and 1 province strategy (refer to Policy ID 104 and P48)', null, '', '1', '0.00', '8', '3', null, null, null, 'These policies are  result of frequent policy dialogues and bottom-up process of scaling CSA and ACIS. Joint outcome on policy evidence of P48 P55 work in My Loi CSV Builds on 5 documents: \r\nI. Farmers Association district to province\r\n (1) Report from the Executive Committee of Ky Anh District Farmer Association submitted to the 9th Congress delegates for 2018 – 2023; sharing lessons learned from international cooperation in My Loi CSV. This led to the uptake of \r\n(2) Ha Tinh province Farmer Association Strategy 2018-2023 (submitted as separate policy), which stressed collaboration with INGOs ICRAF and CARE on CSA and climate adaptation.  \r\nII. Commune People\'s Committee incorporates CSA in New Rural Development Programme, investing in 12 ha CSA-home garden, nursery and CSA for main crops, e.g. rice, peanut. \r\n(3) Ky Son commune People’s committee, November 2018: results on socioeconomics and livelihoods 2018\r\n(4) Ky Son commune Agriculture production plan for 2019 (Ky Son People\'s Committee No 33-KH/UBND of 12 Dec 2018)\r\n(5) Ky Son commune Plan for spring crop 2019 (Ky Son People\'s Committee No 04-DA/UBND 25 Dec 2018) \r\nPolicies #4 & 5 is the written evidence of agriculture planning is informed by seasonal forecasts, rather than climate averages. ', null, '1'), ('1502', '107', '56', '2018', 'Memorandum of understanding (MOU) signed between CIAT and Yen Bai Department of Agriculture and Rural Development (DARD) to officially incorporate Climate Smart Agriculture (CSA) into its provincial development agenda', null, '', '2', '0.00', '5', '3', null, null, null, 'After the courtesy meeting between Yen Bai People\'s Committee and CIAT, the both parties agreed that Yen Bai DARD would be the focal body between CIAT and the Yen Bai government in implementing the CCAFS project. The DARD and the CIAT project team drafted an MOU in Vietnamese and English and signed in July 2016', null, '1'), ('1515', '108', '56', '2018', 'Renewed MOU with the Ministry of Agriculture and Rural Development (MARD) ', null, '', '1', '0.00', null, '3', null, null, null, 'In this renewal, climate change and CSA were added as a priority theme as indication of the national government\'s intent to pursue this in partnership with CIAT', null, '1'), ('1528', '109', '56', '2018', 'MOU with FAO at the regional level', null, '', '1', '0.00', '3', '3', null, null, null, 'The regional workplan for Asia-Pacific includes climate change as one of 4 priority themes, thereby providing a regional-level framework for promoting CSA across countries.', null, '1'), ('1541', '110', '56', '2018', 'Memorandum of understanding (MOU) signed between National Target Program on New Rural Development (NTM) and nine CGIAR centers', null, '', '1', '0.00', '4', '3', null, null, null, 'The intense interactions of the project team as well as the CGIAR centers and CRPs in Vietnam have finally led to an initiative on having a joint MOU between NTM and nine CGIAR centers in boosting CGIAR outcomes, including CCAFS outcomes, in the country. The MOU is signed on 09 March 2019 in Dong Nai province with presence of the Deputy Prime Minister Vuong Dinh Hue, the top leader of NTM, MARD minister Nguyen Xuan Cuong and other MARD top leaders and representatives of nine CGIAR centers', null, '1'), ('1554', '111', '56', '2018', 'Rainforest Alliance Sustainable Agriculture Standard used as a basis for the development of Rainforest Alliance crop-specific certifications', null, 'NGO', '1', '0.00', '3', '3', null, null, null, 'Apart from the use of the training materials developed in the capacity building efforts of RA’s certification work, the approach and thinking of applying risk assessment and suitability mapping at a more granular level down to becoming site specific has found its way into the development of the new agriculture standard of the RA as part of the merger process with UTZ. More detailed climate risk assessments, adaptation planning at the household and community level and consideration of future climatic risks as part of the strategy development for best management practice recommendations at the farm level are likely to be included as requirements in the new standard.\r\n\r\nAs the standard is global in its nature application across the globe should reach the certified farmer base of around 2.4 million farmers. Relevant materials however are being developed one by one, roll out and update across all the farms will take time. The new standard currently in development will come into force in 2020 with a transition period from current to new standard. How many farmers\r\nwill adopt the climate thinking when is difficult to say but will trickle down eventually and as part of continuous improvement orientation of the new standard.', null, '1'), ('1567', '112', '56', '2018', 'Sustainable livestock policy of Colombia\'s national livestock producer organization (FEDEGAN) included information on improved pasture nutrition and methane emissions, which is informing livestock options in the development of the Government of Colombia\'s Nationally Appropriate Mitigation Action policy ', null, '', '1', '0.00', '5', '3', null, null, null, 'FEDEGAN (Colombia’s livestock federation) used results from the RUMINANT model about the contribution of improved pastures to nutritional quality and enteric methane emissions to strengthen its sustainable livestock strategy (https://www.fedegan.org.co/programas/ganaderia-colombiana-sostenible) (Camacho 2019).', null, '1'), ('1580', '113', '57', '2018', 'Inclusion of Morocco and other North African countries in the Economic Community of West African States', null, '', '1', '0.00', null, '3', null, null, null, 'In June 2017, the Economic Community of West African States (ECOWAS) Conference of Heads of State agreed in principle to include Morocco in ECOWAS, but requested a quantitative study on the potential impacts of this change before issuing an official agreement. ECOWAS started to work on this with the United Nations Economic Commission for Africa (UNECA), and UNECA approached IFPRI to conduct this study.\r\n\r\nFollowing a second Heads of State Conference where preliminary results were presented in December 2017, it was decided to deepen the study on Morocco and extend the analysis to Tunisia and Mauritania. IFPRI\'s work aimed to examine the implications of the accession of the three countries to ECOWAS in terms of potential effects of trade creation and diversion and costs and benefits for ECOWAS member countries.\r\n\r\nThe results, showing contrasting impacts on ECOWAS for each joining country, were discussed and shared in preparation of the 54th summit of ECOWAS Heads of State in December 2018. Based on these results, the topic of Morocco\'s membership in ECOWAS was ultimately removed from the summit\'s agenda. This process is still ongoing and sensitive, and IFPRI\'s reports are currently confidential.', null, '3'), ('1593', '114', '56', '2018', 'Investment framework for mobilization of resources into climate smart agriculture (CSA) in Ghana', null, 'Ministry of Agriculture, Ghana', '1', '0.00', null, '3', null, null, null, '', null, '1'), ('1606', '115', '56', '2018', 'Incorporation of project outputs and focus into new proposals by Rainforest Alliance in Ghana, Indonesia and Honduras shows changes in behaviour based on CCAFS results (project details provided in MARLO)', null, '', '1', '0.00', '3', '4', null, null, null, '', null, '1'), ('1619', '116', '56', '2018', 'Impact investor Root Capital introduced CCAFS data on climate change risk into its process for underwriting and prioritizing > $146M of loans to producer organizations in cocoa, coffee etc and promoted uptake by numerous peer agencies in the Council on Smallholder Agricultural Finance (CSAF) community.', null, 'Social investment fund ', '3', '146000000.00', '3', '4', null, null, null, '', null, '1'), ('1632', '117', '57', '2018', 'The Inter-American Development Bank (IDB) used the WEAI to assess the impact of agricultural projects on women\'s impowerment in Nicaragua and Bolivia, and undertook a mid-course correction to improve program design based on the WEAI results found during the first phase of the projects.  ', null, '', '1', '0.00', '1', '3', null, null, null, '', null, '3'), ('1658', '119', '57', '2018', 'USAID Global Food Security Strategy transition framework, guiding USAID\'s investment in food security', null, '', '1', '0.00', null, '3', null, null, null, 'The USAID Global Food Security Strategy (GFSS) transition framework is used to monitor countries\' progress towards an end state where they can transition out of GFSS target country status, thereby allowing better prioritization of the USAID investment in food security. IFPRI\'s contributions to this framework include: 1) assistance in selecting the indicators used to monitor country progress, 2) a tool aggregating indicator data into three composite dimensions (level of need, commitment to food security, and country capacity), and 3) a scorecard approach to visually represent country progress. ', null, '3'), ('1661', '119', '102', '2019', 'USAID Feed the Future Target Country Graduation Policy', 'This policy, which guides the selection of countries to be included in USAID’s Feed the Future initiative, was made publicly available in 2019. It acknowledges IFPRI\'s contribution, ', '', '1', '0.00', null, '4', null, null, '1', '', null, '3'), ('1671', '120', '57', '2018', 'World Bank Ethiopia Economic Update - Inescapable Manufacturing Services Nexus: Exploring the Potential of Distribution Services', null, '', '1', '0.00', null, '3', null, null, null, 'Research conducted by the Ethiopia Strategy Support Program (ESSP) was used to inform the \"Ethiopia Economic Update - Inescapable Manufacturing Services Nexus: Exploring the Potential of Distribution Services\", which looks at Ethiopia\'s recent economic developments, outlook and growth strategy while focusing on the sustainabiltiy of the country\'s investment-focusesed and export-led growth model. ', null, '3'), ('1684', '121', '57', '2018', 'Punjab Agricultural Policy (Pakistan)', null, '', '1', '0.00', null, '4', null, null, null, '', null, '3'), ('1697', '122', '57', '2018', 'US Global Food Security Strategy Bangladesh Country Plan', null, '', '1', '0.00', null, '4', null, null, null, 'USAID extensively consulted IFPRI-PRSSP in the formulation of the new five-year U.S. Global Food Security Strategy (GFSS) Bangladesh Country Plan, which serves as an overarching framework for integrated food security and nutrition programming. The country plan broadly cites IFPRI’s population-level estimates on poverty, women’s empowerment, nutrition, demographics from the BIHS, and IFPRI’s agricultural value chains baseline report.', null, '3'), ('1710', '123', '57', '2018', 'Government of Rwanda\'s Strategic Plan for Agriculture Transformation 2018-2024', null, '', '1', '0.00', null, '4', null, null, null, 'PIM?s research on ?Economywide Impact of Agricultural Growth with PSTA4 Targets,? a government demanded study using the Rwanda CGE Model, has informed the development of Strategic Plan for Agricultural Transformation 2018-24 (called PSTA4), the agricultural strategy and planning document of the government of Rwanda for 2018-2024. Some results of our analysis are directly used in the document (see p23 and p70, e.g.). The authors of this PIM research report were asked to provide direct comments on the PSTA 4 document, which has been finalized and approved by the government in late 2018.', null, '3'), ('1723', '124', '57', '2018', 'Government of Flanders’ strategy on extension', null, '', '1', '0.00', null, '3', null, null, null, 'PIM research on agricultural extensions in Malawi was cited in a Government of Flanders country strategy document, “Country Strategy Paper for Development Cooperation Flanders Malawi 2019-2023” (attached). Agricultural extension is one of the four priority areas under the CSP and the Government of Flanders has earmarked investments of about 6 million EUROs for agricultural extension for Malawi. The results of the project studies show that there are several remaining issues and the Government of Flanders is committed to addressing these issues guided by continued research-based evidence by IFPRI and partners. Within this 5-year CSP, Flanders wishes to build on this momentum due o the initial project impact and is now preparing the second phase of the project. Additionally, the project and two study results were highlighted by the Government of Flanders Country Representative, Nikolas Bosscher, in his speech during the high-level CSP launch in Lilongwe on February 25, 2019. This launch event was attended by the minister of MoAIWD, general representative of the Government of Flanders, government officials, and many donors and international NGOs, among others.\r\n', null, '3'), ('1736', '125', '60', '2018', 'Inclusion of joint village land use planning (JVLUP)  in National Land Use Framework Plan 2013-2033, Tanzania', null, '', '1', '0.00', '8', '4', null, null, null, '', null, '7'), ('1749', '126', '60', '2018', 'Government manual for woreda participatory land use planning in pastoral areas', null, '', '1', '0.00', '8', '4', null, null, null, '', null, '7'), ('1775', '128', '57', '2018', 'Bangladesh\'s Transfer Modality Research Initiative (TMRI)', null, '', '1', '0.00', null, '4', null, null, null, '', null, '3'), ('1788', '129', '59', '2018', 'BFN project', null, '', '1', '0.00', '1', '4', null, null, null, '', null, '5');
INSERT INTO `project_policy_info` VALUES ('1801', '130', '59', '2018', 'Contributions to food systems research agenda for Ethiopia with government and civil society, which in 2018 included ongoing development of food based dietary guidelines and embedding activities in efforts to operationalize the National Nutrition-Sensitive Agriculture Strategy', null, 'Civil society', '1', '0.00', '3', '3', null, null, null, 'The Alive & Thrive program as well as the World Bank have requested the paper to use as a reference in developing the next phase of their programs. ', null, '5'), ('1804', '130', '104', '2019', 'Government of Ethiopia adopts development of food-based dietary guidelines as a priority, government-led program', 'Adoption as a \"flagship program\" requires quarterly progress reports to Parliament, signalling government commitment to providing consumers with advice on dietary patterns that promote health and a healthy food system. ', 'Civil society', '1', '0.00', '3', '3', null, null, '1', 'The process is described here: http://a4nh.cgiar.org/2019/07/10/a-milestone-in-developing-food-based-dietary-guidelines-for-ethiopia/ \r\nThe intent of food-based dietary guidelines (FBDG) is to make available a practical tool that provides recommendations and guidance on types of foods and food groups to be eaten regularly to promote health and prevent chronic diseases. Development of FBDG is an ongoing collaborative effort of A4NH Flagship 1 on Food Systems for Healthier Diets, EPHI, and the Food and Agriculture Organization of the United Nations (FAO). ', null, '5'), ('1814', '131', '59', '2018', 'BFN project', null, '', '1', '0.00', '1', '4', null, null, null, '', null, '5'), ('1827', '132', '59', '2018', 'Committee on World Food Security cites A4NH framework and evidence on nutrition-sensitive value chains (NSVC) in Food Security and Nutrition consultation on NSVC and approach has been adopted by Rome-based agencies to guide their work ', null, 'UN Agencies', '1', '0.00', '3', '3', null, null, null, 'https://marlo.cgiar.org/projects/A4NH/studySummary.do?cycle=Reporting&year=2017&studyID=2221\r\nhttp://www.fao.org/fsnforum/activities/discussions/nsvc\r\n2017 Activity report of the RBA Working Group on Sustainable VCs for Nutrition plus see messages from James Garrett\r\nApproach has been shared with other Rome-based UN agencies through regular meetings and events sponsored by the RBA Working Group on Sustainable Value Chains for Nutrition\"', null, '5'), ('1840', '133', '60', '2018', 'Inclusion of joint village land use planning (JVLUP) in government guidelines on Tools and Spatial Technology for Village Land Use Planning.', null, '', '1', '0.00', '8', '4', null, null, null, '', null, '7'), ('1853', '134', '59', '2018', 'Chaya introduced in school feeding programme in Chiquimula, Guatemala', null, '', '1', '0.00', null, '3', null, null, null, 'REMOVE. Based on SMO comments, removed as a policy and added as an innovation. \r\n\r\nIn 2017, the government of Guatemala approved a School Feeding Law to provide a healthier and more nutritious diet to students between 6 and 12 years of age. This diet will provide 25-35% of the daily dietary requirements of energy and protein that a kid needs. The government allocated a budget of 3 GTQ/day per student in 2018 and contemplates an annual increase in the budget of 1 GTQ, thus for 2019 the budget per student will be 4 GTQ/day.\r\n\r\nA 2018 consultation was held to identify and prepare a list of healthy foods for the public schools located in the Department of Chiquimula. Bioversity International in coordination with Mancomunidad Copan Chorti proposed the native plant known as chaya. Information provided by Bioversity included chaya\'s nutritional value to fight malnutrition, its ease of production, low cost, local availability and different recipes for cooking it (a recipe book was provided by Bioversity). After the government evaluated all the proposed healthy foods, chaya was approved to be a key ingredient in 3 of the 20 menus to be prepared in public schools in Chiquimula in 2019.\r\n\r\nhttps://drive.google.com/open?id=1z0_ARYDt_F-8UOmPnbOoZo8VRwQe4HuA5racwag5qHU\r\n', null, '5'), ('1866', '135', '59', '2018', 'Inter-ministerial Ordinance 284/2018 lists and recognizes the nutritional and economic importance of 100 species of native Brazilian socio-biodiversity. Together with a new “Sociobiodiversity label”, the two instruments incentivize farmers to continue growing/managing and selling local species both via institutional procurement and alternative market channels ', null, '', '1', '0.00', null, '4', null, null, null, 'In Brazil, partners mainstreamed nutritious food biodiversity into policies that support smallholder farmer livelihoods, local agriculture, and improved nutrition, with a focus on institutional procurement. A significant policy achievement - that supports greater conservation and sustainable use of biodiversity for food and nutrition - was the updated Inter-ministerial Ordinance n.283/2018, which adds 100 species to the official list of native Brazilian socio-biodiversity species of nutritional value (http://www.b4fn.org/resources/species-database/). The inclusion of the BFN species on this list can encourage smallholders to conserve, use, produce, and commercialize local biodiversity. ', null, '5'), ('1879', '136', '59', '2018', 'Busia County Biodiversity Policy - the first for Kenya\'s 47 counties - recognizes the importance of native species for nutrition and food security and allocates resources for the conservation of regional food biodiversity, and the incorporation of native species into school meals linked to direct procurement from smallholder farmers. ', null, '', '1', '0.00', null, '4', null, null, null, 'In Kenya, a multi-disciplinary team of farmer organizations, non-governmental organizations, and national and international government agencies have successfully promoted best practices and pilot programs around biodiversity in Kenya. Outcomes include: increasing interest in nutritious African leafy vegetables; the pilot-testing of a food procurement approach that links local producers to schools and health clinics; and the endorsement of a first-of-its-kind biodiversity conservation policy.', null, '5'), ('1892', '137', '62', '2018', 'The conservation agriculture roadmap for India : policy brief', null, '', '1', '0.00', '1', '3', null, null, null, 'Agriculture remains central to the Indian economy, providing livelihood to the majority of its population. Though Indian agriculture have made spectacular progress for food self-sufficiency, yet growing challenges of large management yield gaps, low water and nutrient efficiency, imbalance and inadequate use of external production inputs, diminishing farm profits, deterioration of soil health and environmental quality coupled with climate risks are major concerns. Feeding a growing population with increasing dietary preferences for resource-intensive food products is a major challenge. Moreover, with no scope for horizontal expansion of farming to produce needed food; improving agronomic productivity and achieving high and stable yields under changing and uncertain climate\r\nare important for feeding the growing population. Increasing climatic variability affects most of the biological, physical and chemical processes that drive productivity of agricultural systems. The productivity and stability of agricultural systems depends upon measurable factors and processes controlled by climate and non-climate drivers of production paradigm. It is therefore vitally important to develop strategies and practices to sustainably increase food production while increasing farm income, protecting natural resources and minimizing environmental footprints.  Also, CA need holistic system based approach and multi-disciplinary team efforts and continuum of “Basic-Strategic-Applied Research-Participatory Adaptation & co-learning- Capacity Development', null, '21'), ('1905', '138', '62', '2018', 'Agricultural Policies and Investment Priorities for Managing Natural Resources, Climate Change and Air Pollution', null, '', '1', '0.00', '3', '3', null, null, null, 'Agriculture is an engine of inclusive economic growth as a major source of livelihood for millions of smallholder farmers and other rural residents in India. Having made significant strides in food production, through the Green, White, Yellow, and Blue Revolutions, Indian agriculture is now at a cross road. Deterioration in the quality of natural resources (soil, water and air), together with the adverse effects of climate change, pose significant threats to the sustainability of agricultural production and farmers’ incomes. The situation in the Green Revolution corridors of India is especially daunting with severe problems of hydrological imbalance, soil degradation, and water pollution. In addition, the problem of air pollution from crop residue burning has emerged as a major cause for national and  international concern because of its enormous environmental and health costs across the Northern plains of India. These worrying trends have led policy-makers to recognize that past strategies adopted for agricultural growth need to be re-adjusted, with the benefit\r\nof the same far-sighted vision as in the case of Green Revolution, to address these emerging complex challenges, fully exploit the potential opportunities for inclusive but sustainable growth, and promote rural prosperity', null, '21'), ('1918', '139', '56', '2018', 'Climate Research for Development (CR4D) Africa 2018–2022 Strategic Plan: CR4D is an African-led initiative that aims to strengthen links between climate science research and climate information needs in support development planning across Africa.', null, 'African Climate Policy Center (ACPC), African Ministerial Conference on Meteorology (AMCOMET), World Meteorological Organization (WMO), and Global Framework for Climate Services (GFCS)', '1', '0.00', '10', '4', null, null, null, '', null, '1'), ('1931', '140', '59', '2018', 'The Indian Council of Agricultural Research (ICAR) prioritized nutrition in breeding by establishing minimum levels of iron and zinc to breed in pearl millet', null, '', '1', '0.00', null, '3', null, null, null, 'In 2018, the Indian Council on Agriculture Research established minimum levels of iron and zinc for their pearl millet breeding program, levels informed by joint research with CGIAR. This change means that all future releases of pearl millet varieties in India will be more nutritious and iron and zinc intake will improve among pearl millet consuming populations.', null, '5'), ('1957', '142', '59', '2018', 'Biofortification included in Colombia\'s “Seeds for Peace” program under peace agreements', null, '', '1', '0.00', null, '3', null, null, null, 'REMOVED based on comments from SMO. Save outcomes from the Seeds of Peace/National Program for Replacement of Illicit Crops for a future OICR. \r\n\r\nHarvestPlus was named a partner in the Government of Colombia’s Comprehensive National Program for the Replacement of Illicit Crops (PNIS), which encourages farmers to transition away from growing illegal crops and supports their efforts to grow staple crops for the community. HarvestPlus will be working alongside the Colombian Agricultural Research Corporation (CORPOICA) to ensure high-quality seeds are readily available to farmers seeking to replace their current crops and to provide technical assistance throughout the transition.\r\nhttps://www.harvestplus.org/knowledge-market/in-the-news/harvestplus-participate-colombias-seeds-peace-program', null, '5'), ('1970', '143', '59', '2018', 'World Health Organization Nutrition Policy Review 2016-2017  refers to biofortification and indicates that 20 countries have included biofortification as an action area in their national policies', null, '', '1', '0.00', '9', '3', null, null, null, 'World Health Organization Nutrition Policy Review 2016-2017  refers to biofortification and indicates that 20 countries have included biofortification as an action area in their national policies. https://www.who.int/nutrition/publications/policies/global_nut_policyreview_2016-2017/en/', null, '5'), ('1983', '144', '59', '2018', 'The African Union Specialized Technical Committee on Agriculture endorsed biofortification in 2017, noting its “great potential to contribute significantly” to reducing stunting and other forms of undernutrition. The African Union’s Executive Council endorsed this recommendation in January 2018.', null, '', '1', '0.00', null, '3', null, null, null, ' https://www.harvestplus.org/what-we-do/engagement \r\n\r\nThis paper (below) also confirms that biofortification was discussed among the AU Specialized technical Committee in Agriculture: https://www.cambridge.org/core/services/aop-cambridge-core/content/view/FA20058E095D2C7B7EDC710662253860/S0029665119000521a.pdf/advocacy_for_scaling_up_biofortified_crops_for_improved_micronutrient_status_in_africa_approaches_achievements_challenges_and_lessons.pdf  \r\n\r\n', null, '5'), ('1996', '145', '59', '2018', 'African Development Bank Multi-sectoral Nutrition Action Plan 2018-2025 mentions biofortification among the strategies to mainstream nutrition into their portfolio ', null, '', '1', '0.00', null, '3', null, null, null, 'REMOVED. We reported this in 2017. \r\n\r\nAfDB Multi-sectoral Nutrition Action Plan 2018-2025 mentions biofortification among the strategies to mainstream nutrition into AfDB’s portfolio: \r\nhttps://www.afdb.org/fileadmin/uploads/afdb/Documents/Generic-Documents/Banking_on_Nutrition_ActionPlan_A4_V1d_single.pdf', null, '5'), ('2009', '146', '59', '2018', 'The 10th Africa Task Force on Food and Nutrition Development Meeting recommended that biofortification partners work through the Member States to champion the cause of biofortification at the African Union\'s Specialized Technical Committee on Health, Population, and Drug control', null, '', '1', '0.00', null, '3', null, null, null, 'Link to report is not currently available. ', null, '5'), ('2022', '147', '59', '2018', 'Citi Global Perspectives & Solutions (one of the world\'s largest financial institutions) mentions biofortification as an important nutrition innovation that is particularly relevant to the billions of people in the world whose diets primarily consist of nutrient-deficient staple foods. ', null, '', '1', '0.00', null, '3', null, null, null, 'Biofortification was recently highlighted in a report from Citi Global Perspectives & Solutions, a division of the Citi financial group that analyzes key global trends and challenges through an economic and business lens. This report focuses on Feeding the Future—and how innovation and shifting consumer preferences can help feed a growing planet.\r\nhttps://ir.citi.com/%2FE%2BL%2FXXY1kJ84Yw2opYokGlgNZXi%2FnQE2%2Bs%2BPuRj44Cad1f2Qg8759VoKFfL2qRb0ef4iDSq6Hg%3D\r\nhttps://www.harvestplus.org/knowledge-market/in-the-news/citi-report-highlights-biofortification-nutrition-innovation', null, '5'), ('2035', '148', '59', '2018', 'Progress made on defining \'biofortification\' in Codex Alimentarius', null, 'Regulatory body', '1', '0.00', null, '3', null, null, null, 'Link to report from most recent Codex session available here: https://www.dropbox.com/s/oxikihqqwkgwhwg/CODEX_REP19_NFSDUe.pdf?dl=0  \r\n\r\nLink to previous sessions available here: https://www.ccnfsdu.de/ \r\n', null, '5'), ('2048', '149', '59', '2018', 'Global Child Nutrition Forum, a learning exchange and technical assistance conference held annually to support countries in the development and implementation of sustainable school feeding programs, refers to biofortification (mentioned for the first time) among nutrition-sensitive school feeding programs to address micronutrient deficiency.', null, 'Forum', '1', '0.00', null, '3', null, null, null, 'Global Child Nutrition Forum - refers to biofortification under item #5 (mentioned for the first time) among nutrition-sensitive school feeding programs to address micronutrient deficiency: https://gcnf.org/wp-content/uploads/2018/10/GCNF2018_Communique_EN-1.pdf', null, '5'), ('2061', '150', '59', '2018', 'International Fund for Agricultural Development (IFAD) -  mentions biofortification as a strategic entry point to add nutrition value along the chain ', null, '', '1', '0.00', '7', '3', null, null, null, 'REMOVED based on comments from SMO. Agree that the evidence does not necessarily support an achievement relevant for this indicator.  \r\n\r\nhttps://www.ifad.org/documents/38714170/40197148/Indonesia_brochure.pdf/9ab34286-983d-4b3c-ba85-d6950a50ab68\r\nhttps://www.ifad.org/en/web/knowledge/publication/asset/40271825', null, '5'), ('2074', '151', '59', '2018', 'The Access to Nutrition Foundation - creators of the Access to Nutrition Index - refers to working with HarvestPlus on biofortification and with the Global Alliance for Improved Nutrition (GAIN) in fortification', null, '', '1', '0.00', null, '3', null, null, null, 'REMOVED based on comments from SMO. Agree that the evidence does not necessarily support an achievement relevant for this indicator.  \r\n\r\nLink to report is not currently available. ', null, '5'), ('2087', '152', '57', '2018', 'US Global Food Security Strategy Bangladesh Country Plan (informed by Women\'s Empowerment in Agriculture Index)', null, '', '1', '0.00', null, '4', null, null, null, 'Findings using the Women\'s Empowerment in Agriculture Index (WEAI) suggest women\'s empowerment improves a wide range of household, women and child nutritional outcomes, such as household food security and dietary diversity. These country-specific findings were used to priorize key areas of disempowerment in the design of USAID programs to incease empowerment of women in Feed the Future countries. They have also informed the U.S. Government\'s Global Food Security Strategy.  ', null, '3'), ('2100', '153', '57', '2018', 'Government of Flanders’ strategy on agricultural extension in Malawi', null, '', '1', '0.00', null, '4', null, null, null, 'PIM research on agricultural extensions in Malawi was cited in a Government of Flanders country strategy document, “Country Strategy Paper for Development Cooperation Flanders Malawi 2019-2023” (attached). Agricultural extension is one of the four priority areas under the CSP and the Government of Flanders has earmarked investments of about 6 million EUROs for agricultural extension for Malawi. The results of the project studies show that there are several remaining issues and the Government of Flanders is committed to addressing these issues guided by continued research-based evidence by IFPRI and partners. Within this 5-year CSP, Flanders wishes to build on this momentum due o the initial project impact and is now preparing the second phase of the project. Additionally, the project and two study results were highlighted by the Government of Flanders Country Representative, Nikolas Bosscher, in his speech during the high-level CSP launch in Lilongwe on February 25, 2019. This launch event was attended by the minister of MoAIWD, general representative of the Government of Flanders, government officials, and many donors and international NGOs, among others.\r\n', null, '3'), ('2113', '154', '57', '2018', 'US Global Food Security Strategy Ethiopia Country Plan', null, '', '1', '0.00', null, '4', null, null, null, 'The United States Agency for International Development (USAID) used ESSP research to inform the Global Food Security Strategy (GFSS) Ethiopia Country Plan for 2018-2023, which serves as an overarching framework for integrated food security and nutrition programming in Ethiopia.', null, '3'), ('2126', '155', '57', '2018', 'Alliance for a Green Revolution in Africa (AGRA)\'s Africa Agriculture Status Report 2018', null, 'NGO', '1', '0.00', null, '3', null, null, null, 'The Alliance for a Green Revolution in Africa (AGRA)\'s Africa Agriculture Status Report 2018, which serves as a handbook for governments and their partners to help transform agriculture in Africa, used research from IFPRI\'s Ethiopia Strategy Support Program.  \r\n', null, '3'), ('2139', '156', '57', '2018', 'World Bank Cereal Market Performance in Ethiopia: Policy Implications for Improving Investments in Maize and Wheat Value Chains', null, '', '1', '0.00', null, '3', null, null, null, 'Research conducted by the Ethiopia Strategy Support Program (ESSP) was used to inform the World Bank\'s Cereal Market Performance in Ethiopia: Policy Implications for Improving Investments in Maize and Wheat Value Chains, which seeks to inform and guide project operations for the Government of Ethiopia and the World Bank. ', null, '3'), ('2152', '157', '57', '2018', 'China’s Rural Revitalization Strategy', null, '', '1', '0.00', null, '4', null, null, null, 'A report prepared by the China Strategy Support Porgam using the Statistics on Public Expenditures for Economic Development (SPEED) database was used to support the Asian Development Bank’s (ADB) technical assistance to the People’s Republic of China (PRC). In line with the recommendations made in the report, the PRC has made changes to their agricultural pulic expenditure policies, and emphasized both public and private investment to support agriculture and rural development. ', null, '3'), ('2165', '158', '56', '2018', 'The Cauca Department Secretariat of Women, Colombia,  has updated its Rural Women Policy to include climate change and variability and the role of women in addressing climate and environmental challenges.', null, '', '1', '0.00', '8', '4', null, null, null, 'Secretariat of Women has started the implementation of the Rural Women Policy, updated with the component of climate change and variability. CCAFS-Ecohabitats-CIAT alliance were key in strengthening such policy in order to consider the role of women in promoting changes towards climate-smart agriculture. ', null, '1'), ('2178', '159', '57', '2018', 'The Cambodian Ministry of Agriculture, Forestry, and Fisheries, on the succcess in monitoring our intervention by calling farmers with extension advice, budgeted in 2018 to continue the activity using their own resources from the ASPIRE project, with plans to develop interactive voice lines.', null, '', '3', '30000.00', null, '4', null, null, null, '', null, '3'), ('2191', '160', '56', '2018', 'Gender and Social Inclusion Incorporated into the Kenya Dairy Board Strategic Plan', null, '', '1', '0.00', '8', '3', null, null, null, 'The CCAFS project has supported Kenya Dairy Board in a scoping study for the development of their gender strategy. The study focused on strategies, methodologies, good practices and lessons learned in addressing gender and social inclusion in the dairy sector. As part of this scoping study, numerous discussions with KDB staff (including management) were held on gender and social inclusion in the dairy sector (including youth). Kenya Dairy Board decided to include gender and social inclusion as crosscutting issue in their corporate strategic plan.\r\nThe project continues to support Kenya Dairy Board in the development of their gender strategy. The strategy will be finalized in 2019.\r\n', null, '1'), ('2204', '161', '56', '2018', 'Kenya Climate-Smart Agriculture Project (KCSAP) Monitoring and Evaluation Manual Incorporates CGIAR Methods for GHG Emissions', null, '', '3', '33560000.00', '7', '3', null, null, null, 'The KCSAP Monitoring and Evaluation Manual is an internal project document. \r\nThe document states that, as the Gold Standard Smallholder Dairy Methodology is used as the basis for monitoring in Kenya’s Dairy Nationally Appropriate Mitigation Action (Dairy NAMA), the use of the same methodology is proposed for the KCSAP. Furthermore, the M&E manual states that \"Using this methodology will support Kenya in monitoring results of national climate change mitigation actions\". Amount of investment based on 12% mitigation focus of $279.7 million project investment reported at http://projects.worldbank.org/P154784?lang=en', null, '1'), ('2269', '166', '57', '2018', 'Bangladesh’s Improved Maternity and Lactating Mother Allowance (improved targeting criteria)', null, '', '1', '0.00', null, '4', null, null, null, 'In 2018, upon request of the Ministry of Women and Children Affairs and of the World Food Program, IFPRI/PIM conducted a study funded by the Bill & Melinda Gates foundation to propose selection criteria for the Improved Maternity and Lactating Mother Allowance. The Ministry accepted the recommendations from the study and incorporated them in the program implementation directive.', null, '3'), ('2295', '168', '63', '2018', 'Gender and Diversity Policy', null, '', '1', '0.00', '2', '3', null, null, null, 'CIMMYT BoT approved Gender and Diversity Policy\r\nhttps://www.cimmyt.org/wp-content/uploads/2016/04/GenderDiv-Policy_060219.pdf', null, '22'), ('2308', '169', '62', '2018', 'Gender and Diversity in Research and the Workplace', null, '', '1', '0.00', '1', '3', null, null, null, 'Development of policy, approved by BoT\r\n\r\nhttps://www.cimmyt.org/wp-content/uploads/2016/04/GenderDiv-Policy_060219.pdf\r\n', null, '21'), ('2321', '170', '60', '2018', 'Sustainable livestock policy of Colombia\'s national livestock producer organization (FEDEGAN) included information on improved pasture nutrition and methane emissions, which is informing livestock options in the development of the Government of Colombia\'s Nationally Appropriate Mitigation Action policy', null, '', '1', '0.00', '8', '3', null, null, null, 'In 2018, through the Colombian Roundtable of Sustainable Cattle Production, we started working on the technical basis for elaborating a national public policy on Sustainable Cattle Production for Colombia. Apart from CIAT, several public (e.g. Ministry of Agriculture, Ministry of Environment) and private entities were involved in creating the baseline document. This document is still confidential and not open for sharing. In 2019, we will further advance on the document which will then be presented to the relevant authorities. ', null, '7'), ('2334', '171', '59', '2018', 'CGIAR Antimicrobial Research (AMR) strategy developed', null, '', '1', '0.00', '1', '3', null, null, null, 'Researchers from Flagship 5 provided technical inputs into the special initiative on AMR described in the new CGIAR Business Plan (2019-2021) and played a major role in the development of the CGIAR AMR Strategy (https://www.dropbox.com/s/c0fop87k6iz27bc/CGIAR%20AMR%20strategy.docx?dl=0) along with Centers (ILRI, IFPRI, IWMI, and WorldFish) and at least two other CRPs (Livestock and WLE). ', null, '5'), ('2386', '175', '56', '2018', 'Communicating yield gap research outputs to policy makers and extension agents in Ethiopia', null, '', '1', '0.00', '8', '3', null, null, null, 'The Global yield Gap Atlas, IMAGINE and Crop Nutrient Gap projects are active in Ethiopia. These projects are complementary and outputs are communicated to policy makers, extension and development actors by a team from CIMMYT and the Ethiopian Institute of Agricultural Research (EIAR). \r\n\r\nYield gaps, their causes and opportunities to narrow yield gaps were presented to the Minister of the Ministry of Agriculture and Natural Resources on 15 March 2018. The minster saw immediately the importance of the results, particularly to the extension system. He asked to present the results to the annual national development agent training of trainers (> 300 participants) in his presence on 26 March 2018. This was highly appreciated and follow-up presentations were made to trainings organized at regional state level March-April 2018 in Oromia, Amhara and Southern Nations and Nationalities regional states. \r\n\r\nA few quotes from the participants: “these are interesting results that show the gap and motivate us to work towards increasing productivity in our respective regions”. The Federal Agricultural Extension Directors stated that “these are the kind of research evidence that we need to guide our extension services in the country and we will keep in touch with the research team”. \r\n', null, '1'), ('2425', '178', '56', '2018', 'Advisory provided for the release of the resolution 1447 of 2018 that regulates the MRV system of mitigation actions at the national level in Colombia', null, '', '1', '0.00', '8', '4', null, null, null, '', null, '1'), ('2477', '182', '56', '2018', 'Investment in Climate Smart Livestock in East Africa by German government', null, 'German government investment in climate smart livestock', '3', '200000.00', '8', '4', null, null, null, '', null, '1'), ('2490', '183', '189', '2018', 'Expansion of the \'seeds without borders\' agreement to Bhutan. Bhutan can now import improved variety seeds without much hassle from six countries in the region. Bangladesh, Cambodia, India, Myanmar, Nepal, and Sri Lanka are the other members of the network agreement', null, 'Governments', '1', '0.00', '8', '4', null, null, null, 'Expansion of the seeds without borders agreement to Bhutan (News Kuensel). Bhutan can now import improved variety seeds without much hassle from six countries in the region. The country joined the Seeds Without Borders protocol with the agriculture secretary Rinzin Dorji formally signing the agreement on June 13 in Thimphu. Bangladesh, Cambodia, India, Myanmar, Nepal, and Sri Lanka are the other members of the network agreement. The agreement initiated by International Rice Research Institute (IRRI) was first signed between Bangladesh, and India in 2013. The agreement started with rice seeds. The seed agreement covers a number of rice-based crops such as maize, wheat, vegetables, pulses and other crops with the possibility to include many more in future.\r\n\r\nhttp://www.kuenselonline.com/bhutan-joins-seeds-without-borders/', null, '16'), ('2516', '185', '56', '2018', 'Revolving credit fund for farmers of Northern Bandung Dairy Farmer Cooperative (KPSBU) to make investments for improved manure management', null, '', '3', '0.00', null, '3', null, null, null, '', null, '1'), ('2555', '188', '56', '2018', 'FMARD commitment to agCelerant implementation for 15 million smallholder farms', null, '', null, '0.00', null, null, null, null, null, '', null, '1'), ('2581', '190', '57', '2018', 'Philippines Development Plan 2017-2022', null, '', '1', '0.00', null, '4', null, null, null, '', null, '3'), ('2646', '195', '60', '2018', 'Rwanda Livestock Master Plan', null, '', '1', '0.00', '8', '4', null, null, null, '', null, '7'), ('2659', '196', '60', '2018', 'National guidelines for agri-nutrition community messaging in Kenya ', null, '', '1', '0.00', '8', '3', null, null, null, 'AVCD nutrition work has supported the development of new national Agri-nutrition guidelines for community messaging in Kenya \r\nSee the community dialogue cards on agri-nutrition https://cgspace.cgiar.org/handle/10568/98538\r\nThe Government of Kenya contributed and the logo is on the cards', null, '7'), ('2672', '197', '57', '2018', 'Agricultural Transformation Agency\'s Direct Seed Marketing Program (Ethiopia)', null, '', '1', '0.00', null, '4', null, null, null, '', null, '3'), ('2685', '198', '60', '2018', 'Commitment by Ethiopian Institute of Agricultural Research to invest in gender training', null, '', '3', '0.00', '1', '3', null, null, null, 'Scientists from EIAR, CIMMYT, ICARDA and ILRI conducted a gender training at EIAR headquarters. Following this training, EIAR conducted on their own a similar training, using a ToT approach. While a monetary value cannot be attached to EIAR investment to increase gender awareness and understanding in their centres, a total of 244 researchers (185 men and 59 women), as well as 185 administrative staff (117 men and 68 women) were trained for 2 days. Topics included: importance of gender; gender analysis and mainstreaming; gender diversity and Inclusion; gender analysis before research proposal writing; gender in plant breeding; gender responsive research; gender transformative research; gender in agriculture research.  \r\nCurrently EIAR has invested in sending people to training organised by the CGIAR. Their commitment to invest their own resources for future training is not yet confirmed (hence level 1). \r\nLetter requesting support:  https://cgiar.sharepoint.com/sites/livestock/Shared%20Documents/Forms/AllItems.aspx?viewpath=%2Fsites%2Flivestock%2FShared%20Documents%2FForms%2FAllItems%2Easpx&id=%2Fsites%2Flivestock%2FShared%20Documents%2FAnnual%20Reports%2F2018%20Annual%20Report%2FEvidence%20%28Deliverables%2C%20Innovations%2C%20Policies%202018%29%2FP198%20EIAR%20Gender%20Responsive%20Research%20Capacity%20Building%20Support%2Epdf&parent=%2Fsites%2Flivestock%2FShared%20Documents%2FAnnual%20Reports%2F2018%20Annual%20Report%2FEvidence%20%28Deliverables%2C%20Innovations%2C%20Policies%202018%29.', null, '7'), ('2698', '199', '57', '2018', 'National Agriculture Investment Plan for Malawi', null, '', '1', '0.00', null, '4', null, null, null, '', null, '3'), ('2724', '201', '59', '2018', 'Supported development of the 2019-2025 Nutrition Action Plan for the International Fund for Agricultural Development (IFAD)', null, '', '1', '0.00', '10', '3', null, null, null, 'REMOVED based on comments from SMO. Will save this for the future when it is published and/or describe the achievements of the overall partnership with IFAD as an OICR in the future.\r\n\r\nProvided expert review during development of the 2019-2025 Nutrition Action Plan for the International Fund for Agricultural Development (IFAD). Building on understanding of IFAD\'s organizational culture and needs derived from the multi-year A4NH partnership to support mainstreaming nutrition at IFAD, comments reflected the range of research outputs and evidence produced by A4NH and others on nutrition and smallholder agriculture as well as operational experiences. \r\n\r\nWritten and oral comments are part of internal processes and could be provided upon request. \r\nThe Nutrition Action Plan will be presented to Board in April 2019.  No public document is available yet.', null, '5'), ('2737', '202', '59', '2018', 'Highlighted the importance and relevance of nutrition for the Sustainable Development Goals (SDGs) for the High Level Political Forum with a background paper on how nutrition relates to SDG 11 ', null, '', '1', '0.00', '10', '3', null, null, null, 'REMOVED based on comments from SMO. May save outcomes from HLPE recommendations (based on A4NH evidence) as an OICR for the future. \r\n\r\nPrepared a background paper on how nutrition relates to SDG 11 (urban settlements ) as part of an Expert Group Meeting sponsored by the UN Standing Committee on Nutrition (UN SCN) as a way to highlight the importance and relevance of nutrition for the SDGs for the High Level Political Forum (HLPF).  The HLPF is a member state forum (ministerial level and above) of the UN that annually reviews progress on achieving the SDGs.  Key messages from the Expert Group Meeting were delivered to the HLPF by the SCN Coordinator in July 2018.  A4NH further facilitated involvement of the CRP on Water, Land and Ecosystems (WLE) in preparation of a similar background document and participation in the Expert Group Meeting. \r\n\r\nSee the link for Expert Group Meeting linking nutrition with the SDGs. It also provides a good description of policy uptake / influence: https://www.unscn.org/en/news-events/upcoming-events?idnews=1828\r\n\r\nWe provided a background paper on SDG11 (Annex 3) and facilitated participation by WLE (background paper on SDG6 – Annex 1)): https://www.unscn.org/uploads/web/news/EGM-Background-Document.pdf\r\n \r\n Key messages (provided to HLPF and others): https://www.unscn.org/uploads/web/news/Final-Report-of-EGM-on-nutrition.pdf\r\n', null, '5'), ('2750', '203', '59', '2018', 'Coursework as part of the PhD module on Food-Health Safety Policy at Addis Ababa University in Ethiopia references A4NH research', null, '', '4', '0.00', '1', '3', null, null, null, ' The following A4NH publications were used as reading materials that informed the curriculum content of the module. [This is not yet formalized at the university, so we will hold off on reporting this as Level 2 until that is the case.]  \r\n\r\n1. Covic, N and Hendriks, S. L. (2016). Achieving a nutrition revolution for Africa: the road to healthier diets and optimal nutrition (Vol. 2015). Intl Food Policy Res Inst. http://dx.doi.org/10.2499/9780896295933   \r\n\r\n2. Gillespie, Stuart; Hodge, Judith; Yosef, Sivan; and Pandya-Lorch, Rajul (Eds.) 2016. Nourishing millions: Stories of change in nutrition. Washington, D.C.: International Food Policy Research Institute (IFPRI). http://dx.doi.org/10.2499/9780896295889_18   \r\n\r\n3. Kennedy, E., Tessema, M., Hailu, T., Zerfu, D., Belay, A., Ayana, G., ... & Kassaye, T. (2015). Multisector nutrition program governance and implementation in Ethiopia: opportunities and challenges. Food and nutrition bulletin, 36(4), 534-548.  \r\n\r\n4. Warren⁎ A. M. and Frongilloo E. A. Mid-level actors and their operating environments for implementing nutrition-sensitive programming in Ethiopia. Global Food Security 13 (2017) 66–73. https://www.sciencedirect.com/journal/global-food-security/vol/13   \r\n\r\n5. Headey D. Hoddinott, J. and Park S. 2016 Accounting for nutritional changes in six success stories: A regression decomposition approach. Global Food Security 13 (2017) 12–20. https://www.sciencedirect.com/journal/global-food-security/vol/13', null, '5'), ('2763', '204', '59', '2018', 'Training at Lal Bahadur Shastri National Academy of Administration (India) on nutrition for district administrators , held by Partnerships and Opportunities to Strengthen and Harmonize Actions for Nutrition in India (POSHAN) ', null, '', '4', '0.00', '1', '3', null, null, null, 'REMOVED based on comments from SMO. May save outcomes of the curriculum/ training (which is based on A4NH evidence) and/or how A4NH policy engagement has led to increased demand for curriculum/training for a future OICR.\r\n\r\n\r\nA district magistrate credited the course for raising his awareness about the Village Health Sanitation and Nutrition Day (VHSND), which is an existing Government initiative to improve access to nutrition and sanitation services at the village level in India', null, '5'), ('2766', '204', '104', '2019', 'Training at Lal Bahadur Shastri National Academy of Administration (India) on nutrition for district administrators , held by Partnerships and Opportunities to Strengthen and Harmonize Actions for Nutrition in India ', 'POSHAN collaborated with partners to develope nutrition case studies for training Indian Administrative Services (IAS) officials', '', '4', '0.00', '1', '3', null, null, '0', 'The Partnerships and Opportunities to Strengthen and Harmonize Actions for Nutrition in India (POSHAN) initiative held two workshops (August and December 2019) to strengthen capacity building efforts and assess further needs of the Lal Bahadur Shastri National Academy of Administration, India’s premier training institution for civil services. In 2019, POSHAN, which is led by IFPRI and is a major bilaterally-funded project in A4NH, initiated a process of developing a roadmap for capacity building on nutrition for administrators across sectors and administrative levels. These capacity building efforts complement the two nutrition short courses (August 2018 and February 2019) POSHAN conducted, which were the first of their kind, for district level functionaries.\r\n', null, '5'), ('2776', '205', '59', '2018', 'Ten Krishi Vigyan Kendras (agriculture extension offices) in two Indian States are setting up Farming System for Nutrition (FSN) models. ', null, '', '1', '0.00', '8', '3', null, null, null, 'REMOVED based on SMO comments, we\'re removed this as a policy and added it as an innovation. \r\n\r\nWardha district, Maharashtra, India was one of the sites of a research study by the Leveraging Agriculture for Nutrition in South Asia (LANSA) partnership to demonstrate the feasibility of a farming systems approach to address the problem of undernutrition. The endline survey in 2017 showed evidence of improvement in dietary diversity. Farmers have emerged as spokespeople and shared their experience at block and district level forums. Farmers from another 16 villages have been motivated to practise similar interventions.\r\n\r\nhttps://icar.org.in/content/operationalising-farming-system-nutrition-through-kvks\r\nhttp://lansasouthasia.org/blog/farming-system-nutrition-demonstration-models-maharashtra-%E2%80%93-thanks-lansa-%E2%80%98champion%E2%80%99 \r\n', null, '5'), ('2789', '206', '59', '2018', 'Minutes of the Leveraging Agriculture for Nutrition in South Asia (LANSA) and NITI Aayog dialogue on Leveraging Agriculture for Nutrition were circulated to government officials (in India) with action points to move forward on the nutrition-sensitive agriculture agenda.', null, '', '1', '0.00', '3', '3', null, null, null, 'REMOVED based on comments from the SMO. Will save this for the future (maybe as an OICR or Table 2) when more outcomes from these specific action points in the nutrition-sensitive agenda in India have been achieved. \r\n\r\nhttp://lansasouthasia.org/article/niti-aayog-and-lansa-seek-better-agriculture-nutrition-linkages-india\r\nhttp://lansasouthasia.org/sites/default/files/NITIAayog-MSSRF_Copy%20of%20Final%20Minutes_Jan2018_0.pdf\r\n', null, '5'), ('2802', '207', '59', '2018', 'The National Implementation Platforms for Nutrition (NIPN) changed their data analysis approach incorporating recommendations based on accumulated A4NH experience from impact evaluations and data analyses. NIPN is designed to strengthen country capacity to analyse data to understand better how malnutrition can be prevented and to inform national policies and programmes.  ', null, 'NIPN', '1', '0.00', '2', '3', null, null, null, 'For the purposes of this indicator, the data analysis approach is viewed as a critical part of NIPN\'s strategy. The approach has not been written down as such (to our knowledge), so this achievement remains Level 1. However, documents supporting their data analysis approach are posted online on their website under Trainings/Data Management (http://www.nipn-nutrition-platforms.org/Trainings) and Guidelines/Data Analysis (http://www.nipn-nutrition-platforms.org/Project-resources). NIPN documents and correspondence from the Flagship 4 representative on the NIPN Expert Advisory Group could be provided upon request.\r\n\r\n1. http://www.nipn-nutrition-platforms.org/Trainings\r\n2. http://www.nipn-nutrition-platforms.org/Project-resources\r\n', null, '5'), ('2815', '208', '59', '2018', 'The Bangladesh National Food Policy and Country Investment Plans 2015, 2016 and 2017 made direct reference to the Leveraging Agriculture for Nutrition in South Asia (LANSA) initiative, and the focus in the next phase is on nutrition-sensitive food systems', null, '', '1', '0.00', '2', '3', null, null, null, 'REMOVED based on comments from the SMO. We recognize we don\'t have enough of or the right evidence for 2018 reporting  and comments seem to suggest the OICR as currently prepared was also not adequate. We may examine how this can be strengthened for reporting in 2019. \r\n\r\nThe Bangladesh National Food Policy and Country Investment Plans over three years (2015-17) made direct reference to Leveraging Agriculture for Nutrition in South Asia (LANSA) research, with the focus in the next phase being on nutrition-sensitive food systems.', null, '5'), ('2828', '209', '57', '2018', 'USAID Nobo Jatra (New Beginning) project in Southern Bangladesh (inclusion of nutrition training component)', null, '', '3', '74000000.00', null, '4', null, null, null, '', null, '3'), ('2841', '210', '59', '2018', 'Bill and Melinda Gates Foundation and UK Department for International Development make major joint investment in food safety research for development ', null, '', '3', '13000000.00', null, '4', null, null, null, 'OICR2730:\r\nThe Bill & Melinda Gates Foundation and the UK Department for International Development used cumulative ILRI evidence on food safety burden and management in informal markets to shape their first joint investment in food safety. In response to the call, six grants totalling $13 million were awarded. Evidence-based interventions will be tested at a large-scale, potentially reducing food-borne disease risks for millions of consumers in Ethiopia, Burkina Faso, Kenya, Mozambique, Nigeria, Tanzania and one state in India. ', null, '5'), ('2854', '211', '189', '2018', 'Contribution to the Rice Industry Roadmap Plan 2030 in the Philippines', null, 'Governments', '1', '0.00', '8', '4', null, null, null, 'The Philippines Rice Industry Roadmap 2030 (PRIR 2030) draws heavily from the research output of the “Benchmarking the Philippine Rice Economy Relative to Major Rice Producing Countries in Asia”, a collaborative project of IRRI and PhilRice funded by the Philippine Department of Agriculture. The PRIR 2030 was drafted in September 2018 with the aim to provide food security to the Philippines. The main challenges mentioned are a rapidly increasing population, rising living standards, climate change and water scarcity (http://www.philrice.gov.ph/wp-content/uploads/2018/09/The-Philippine-Rice-Industry-Roadmap-2030.pdf).  Rice is considered at the center of the Philippine economy and national and international policies are governing markets and tariffication. The research conducted by IRRI together with PhilRice reflects into investments and policies around the rice industry in the Philippines and assesses the level of competitiveness of the Philippine rice in relation to the major rice exporting countries in Asia (Thailand, Vietnam, Cambodia and Myanmar) (http://books.irri.org/Cross_country_research.pdf).  This study was used by the Department of Agriculture of the Philippines to build the PRIR 2030.', null, '16'), ('2867', '212', '59', '2018', 'East African Community (EAC) Policy Briefs on Aflatoxin Prevention and Control launched and endorsed', null, 'Regional organization', '1', '0.00', null, '4', null, null, null, 'The East African Community officially launched nine policy briefs on topics related to crop contamination with aflatoxins and its control. The policy briefs contain key recommendations on strategic policy action and interventions required to mitigate impacts and effects of aflatoxin along the food and feed value chains.', null, '5'), ('2880', '213', '57', '2018', 'Rice Tariffication Law (Philippines)', null, '', '2', '0.00', null, '3', null, null, null, 'An IFPRI study supported by PIM and CCAFS showed that removing rice subsidies and replacing quantitative restrictions on rice imports with tariffs would boost economic growth in the Philippines and foster climate change mitigation. The corresponding rice tariffication reform was passed by the House and Senate in 2018 (and signed into law in 2019).', null, '3'), ('2893', '214', '57', '2018', 'US Global Food Security Strategy Ethiopia Country Plan (informed by Women\'s Empowerment in Agriculture Index)', null, '', '1', '0.00', null, '4', null, null, null, '', null, '3'), ('2906', '215', '57', '2018', 'US Global Food Security Strategy Nigeria Country Plan (informed by Women\'s Empowerment in Agriculture Index)', null, '', '1', '0.00', null, '4', null, null, null, '', null, '3'), ('2919', '216', '57', '2018', 'US Global Food Security Strategy Honduras Country Plan (informed by Women\'s Empowerment in Agriculture Index)', null, '', '1', '0.00', null, '4', null, null, null, '', null, '3'), ('2932', '217', '57', '2018', 'US Global Food Security Strategy Kenya Country Plan (informed by Women\'s Empowerment in Agriculture Index)', null, '', '1', '0.00', null, '4', null, null, null, '', null, '3'), ('2945', '218', '57', '2018', 'Curriculum for biosafety inspectors in Malawi', null, '', '4', '0.00', null, '4', null, null, null, '', null, '3'), ('2958', '219', '57', '2018', 'Pakistan National Food Security Policy', null, '', '1', '0.00', null, '4', null, null, null, '', null, '3'), ('2971', '220', '57', '2018', 'World Bank\'s Income Support Program for the Poorest Project (improved targeting criteria)', null, '', '3', '300000000.00', null, '3', null, null, null, 'In an e-mail, World Bank\'s Social Protection Economist Anneka Rahman confirmed that the World Bank-financed Income Support Program for the Poorest will incorporate most of IFPRI\'s  proposed targeting criteria.', null, '3'), ('2984', '221', '59', '2018', 'Federal Ministry of Economic Cooperation and Development (BMZ) makes a major commitment to invest in food safety research for development in Uganda', null, '', '3', '0.00', null, '3', null, null, null, ' BMZ made a major commitment to invest in food safety research for development in Uganda. Results and recommendations from multi-year projects in Uganda, some funded by BMZ/GIZ like mPig (https://www.ilri.org/node/39617 ) and the Safe Food Fair Food project (https://safefoodfairfood.ilri.org/about/ ) carried out by Flagship 3/A4NH helped shape this investment.', null, '5'), ('2997', '222', '59', '2018', 'As a result of engagement with the East African Community (EAC), curriculum on food safety in informal markets is included in continuing professional development value chain training with the Food and Agriculture Organization of the United Nations (FAO)', null, 'United Nation agencies', '4', '0.00', '10', '3', null, null, null, 'Curriculum and email correspondence can be provided upon request. ', null, '5'), ('3010', '223', '59', '2018', 'The World Bank\'s Safe Food Imperative: Accelerating Progress in Low- and Middle-Income Countries report provides countries with a guide to avoiding the burden of unsafe food—including the right type of investments, policies, and other interventions.', null, '', '1', '0.00', '7', '3', null, null, null, 'A4NH researchers provide contributions to this major report from the World Bank, drawing more attention to the issue of food safety in low and middle income countries and demonstrating that the burden of unsafe food can be avoided through practical and often low-cost behavior and infrastructure changes at different points along food value chains, including in traditional food production and distribution channels. \r\nhttps://www.worldbank.org/en/topic/agriculture/publication/the-safe-food-imperative-accelerating-progress-in-low-and-middle-income-countries\r\n', null, '5'), ('3036', '225', '59', '2018', 'Implementation of National Action Plan for antimicrobial resistance (AMR) in Kenya', null, '', '1', '0.00', '8', '3', null, null, null, 'REMOVED based on comments from SMO. Will save this for the future when more specific regulations or actions are in place (for Table 2) and/or when outcomes from the National Action Plan can be demonstrated and described in an OICR. \r\n\r\n\r\nCGIAR/A4NH is helping to implement this plan based on research experience and lessons learned in Kenya. There was less influence/contributions on the plan itself. Correspondence and evidence of participation and contributions can be provided upon request. ', null, '5'), ('3049', '226', '59', '2018', 'Agriculture, Nutrition, and Health (ANH) Academy and ANH Academy Week Conference provide a unique, interdisciplinary conference and community to engage stakeholders around ANH ', null, 'Academia and research', '4', '0.00', '2', '3', null, null, null, 'REMOVED based on comments from the SMO. We\'ve removing this as a policy and added it as an innovation. \r\n\r\nDissemination of methods, metrics, and tools is accelerated through the ANH Academy community and annual conference. Influence of these activities was described in the mid-term evaluation of the London School of Hygiene and Tropical Medicine (LSHTM)-led project called Innovative Methods and Metrics for Agriculture and Nutrition Actions (IMMANA), which facilitates the ANH Academy and A4NH co-leads the annual conference. This evaluation can be provided upon request. \r\n\r\nPast conference reports are available here: https://www.anh-academy.org/anh-academy-week', null, '5'), ('3062', '227', '59', '2018', 'The Global Food Safety Partnership (GFSP) is a  public-private initiative hosted by the World Bank; its 2018 Food Safety in Africa report, to which CGIAR contributed, recommended investment into public health-focused programs aimed at informing and empowering African consumers about food safety to drive market incentives for higher standards.', null, 'Multi-stakeholder platform', '1', '0.00', null, '3', null, null, null, 'A4NH/FP3 researchers are active members of the World Bank\'s Global Food Safety Partnership and contributed to the Food Safety in Africa report. The report, launched ahead of the first International Food Safety Conference in Addis Ababa, Ethiopia (which A4NH/FP3 also helped to coordinate) highlighted the need for more targeted investment to promote food safety at a domestic level across Africa, where foodborne illnesses claim an estimated 137,000 lives a year, according to figures from the World Health Organization (WHO).\r\n\r\nhttps://www.gfsp.org/african-consumers-need-more-investment-food-safety-tackle-high-human-cost-foodborne-illness-new-gfsp\r\n', null, '5'), ('3075', '228', '59', '2018', 'Six countries - Malawi, Nigeria, Tanzania, Senegal, Gambia and Uganda - prioritized aflatoxin mitigation strategies, including use of Aflasafe, and mainstreamed them into National Agriculture and Food Security Investment Plans', null, '', '1', '0.00', null, '3', null, null, null, 'Six countries where the Partnership for Aflatoxin Control in Africa (PACA) works - Malawi, Nigeria, Tanzania, Senegal, Gambia and Uganda - prioritized aflatoxin mitigation strategies, including use of Aflasafe as a mitigation tool, developed stakeholder-aligned aflatoxin control action plans, and mainstreamed them into National Agriculture and Food Security Investment Plans.', null, '5'), ('3088', '229', '58', '2018', 'Based on the demonstrated proof of concept of water spreading to manage extreme flooding across pastures in Afar state, eastern Ethiopia, supported by WLE, the approach is being integrated into a major World Bank/ International Fund for Agricultural Development project ', null, '', '3', '456000000.00', '8', '4', null, null, null, '', null, '4'), ('3101', '230', '58', '2018', 'Indian and Gujarat state governments rolling out national solar irrigation investment program based on results of WLE and CCAFS-supported pilot study. Farmers will be able to sell surplus power to electric utility ', null, '', '3', '16400000000.00', '8', '4', null, null, null, '', null, '4'), ('3114', '231', '58', '2018', 'Based on a WLE-supported evaluation of the effectiveness of an irrigation management training program in Tajikistan, the United States Agency for International Development re-designed the program to target women, who are increasingly taking responsibility for management of the irrigation systems ', null, '', '1', '0.00', '3', '4', null, null, null, '', null, '4'), ('3127', '232', '58', '2018', 'A WLE-supported methodology for monitoring environmental water flows for Sustainable Development Goal 6.4.2 has been adopted and disseminated by the United Nations for use in country reporting against the SDG Goal', null, 'UN', '1', '0.00', '10', '4', null, null, null, '', null, '4'), ('3140', '233', '58', '2018', 'Supported by WLE and CCAFS, the South Asia Drought Monitoring System is being used by the Indian government to make cropping recommendations to farmers, target drought relief efforts and develop district contingency plans', null, '', '1', '0.00', '3', '3', null, null, null, '', null, '4'), ('3153', '234', '58', '2018', 'In Amazonian Colombia, the Financing Fund for the Agriculture Sector (FINAGRO) is testing a methodology developed with WLE support to help smallholders implement forest conservation practices ', null, '', '1', '0.00', '7', '3', null, null, null, '', null, '4'), ('3166', '235', '58', '2018', 'Soil-plant spectral technology guiding soil fertility investments in 17 African countries and several Asian countries ', null, '', '1', '0.00', '1', '4', null, null, null, '', null, '4'), ('3179', '236', '57', '2018', 'Forestry Curriculum, National Agrarian University La Molina, Peru', null, '', '4', '0.00', null, '4', null, null, null, 'With support from PIM and FTA, CIFOR partnered with the Forestry Department of the National Agrarian University La Molina in Peru to study land and forest tenure security of indigenous communities in the Peruvian Amazon using multi-method participatory research.\r\nThe outcomes of this process include increased knowledge and research capacity of university staff on tenure, gender, and indigenous issues, and incorporation of this knowledge into coursework and student theses. A new, mandatory, social science concentration was added to the university’s Forest Engineer curriculum, with the purpose of “evaluat[ing] the natural, social, economic, and political reality of the environment in which we work, for land use planning and governance in the management of forest resources and wildlife, promoting an intercultural, inclusive and multidisciplinary approach”.', null, '3'), ('3192', '237', '60', '2018', 'Progress towards development of policies that enhance private sector investment in animal health service delivery in Kenya ', null, '', '2', '0.00', '6', '3', null, null, null, 'The first step was a review of the animal health laws to find out if they constrain private sector investment: https://hdl.handle.net/10568/100491. They did not prohibit private sector but they were also not supportive. Arising from that review, we are currently discussing with the Director of Veterinary Services a review and consolidation of various veterinary laws to ensure private sector interests are included.', null, '7'), ('3205', '238', '60', '2018', 'World Organisation for Animal Health (OIE) policy analysis to develop typology of public private partnership models for veterinary service delivery, as input to development of practical guidelines', null, '', '1', '0.00', '10', '4', null, null, null, 'The flagship contributed to and reviewed World Animal Health Organisation guidelines for public private partnerships in veterinary service delivery, based on experiences with PPPs in CRP-supported activities (ITM scaling, veterinary mobile runs, PPPs in Ethiopia).', null, '7'), ('3218', '239', '60', '2018', 'CGIAR Antimicrobial Research (AMR) strategy developed', null, '', '1', '1300000.00', '1', '3', null, null, null, 'Researchers from Flagship 2 provided technical inputs into the special initiative on AMR described in the new CGIAR Business Plan (2019-2021) and played a major role in the development of the CGIAR AMR Strategy (https://www.dropbox.com/s/c0fop87k6iz27bc/CGIAR%20AMR%20strategy.docx?dl=0) along with Centers (ILRI, IFPRI, IWMI and WorldFish) and at least two other CRPs (A4NH and WLE).', null, '7'), ('3231', '240', '60', '2018', 'World Organisation for Animal Health (OIE) guidelines on prioritization of animal diseases for which the use of vaccines could reduce antimicrobial use', null, '', '1', '0.00', '10', '4', null, null, null, 'The flagship contributed to the World Animal Health Organisation initiative to develop guidance on prioritization of diseases for which the use of vaccines could reduce antimicrobial use in animals.', null, '7'), ('3257', '242', '61', '2018', 'Draft agroforestry policy for Nepal at final stages of approval in government.', null, '', null, '0.00', null, '3', null, null, null, '', null, '11'), ('3270', '243', '61', '2018', 'Draft Agroforestry Policy and Manual for ASEAN.  At approval Level with ASEAN Ministerial Council', null, 'Communities', '1', '0.00', '8', '3', null, null, null, '', null, '11'), ('3283', '244', '61', '2018', 'Strategy informing African Orphan Crops Consortium/ Nepad on course of action for improving human nutrition in Africa.  Supporting human nutrition in Africa through the integration of new and orphan crops into food systems', null, '', '1', '0.00', '3', '3', null, null, null, 'DOI: http://dx.doi.org/10.5716/WP18003.PDF\r\n\r\nStrategy informing AOCC/Nepad on course of action for improving human nutrition in Africa.\r\n\r\n\r\n', null, '11'), ('3296', '245', '61', '2018', 'Strategy on how access and benefit sharing stimulate interdisciplinary research for tropical biology and conservation. \"Bridging molecular genetics and participatory research\"', null, '', '1', '0.00', '2', '3', null, null, null, 'BIOTROPICA 50(1): 178–186 2018 10.1111/btp.12515\r\n\r\n', null, '11'), ('3309', '246', '61', '2018', 'Strategy informing FAO Global Plan of Action for the conservation, sustainable use and development of forest genetic resources within the framework of the Commission on Plant Genetic Resources for Food and Agriculture. title: \"Indicators of Genetic Diversity of Trees -State, Pressure, Benefit and Response. State of the World’s forest GeneticResources\"', null, '', '1', '0.00', '3', '3', null, null, null, 'Strategy informing the FAO Global Plan of Action for the conservation, sustainable use and development of forest genetic resources within the framework of the Commission on Plant Genetic Resources  for Food and Agriculture.', null, '11'), ('3322', '247', '61', '2018', 'Strategy informing national tree seed systems with respect to Forest Landscape Restoration. Strategy Title: \"Fit for purpose seed supply systems for the implementation of landscape restoration under Initiative 20x20\"', null, '', '1', '0.00', '3', '3', null, null, null, 'https://www.bioversityinternational.org/fileadmin/user_upload/Fit-for-purpose_Atkinson__may2018.pdf\r\n\r\nStrategy informing national tree seed systems with respect to Forest Landscape Restoration', null, '11'), ('3335', '248', '61', '2018', 'The African Plant Breeding Academy (AfPBA). Curriculum to train practicing African plant breeders in the most advanced theory and technologies for plant breeding in support of critical decisions for crop improvement.', null, '', '4', '0.00', '2', '3', null, null, null, 'http://pba.ucdavis.edu/PBA_in_Africa/AfPBA_Curriculum/', null, '11'), ('3361', '250', '61', '2018', 'Strategy on the role of coffee and cocoa in deforestation and tree cover change', null, '', '1', '0.00', '3', '3', null, null, null, 'This publication is part of a book by the World Bank aimed at helping the WB to steer funding and support to both governments and private sector (coffee and cocoa) to address issues related to the production of these commodities without deforestation or loss of tree cover due to intensification and expansion of crop production. ', null, '11'), ('3374', '251', '61', '2018', 'Draft Agroforestry Policy and Manual for for the Association of Southeast Asian Nations ( ASEAN).  At approval Level with ASEAN Ministerial Council', null, '', '1', '0.00', '4', '3', null, null, null, 'http://www.worldagroforestry.org/region/sea/publications/detail?pubID=4353\r\n\r\nhttp://www.worldagroforestry.org/blog/2019/01/16/asean-guidelines-for-agroforestry-development-set-to-revolutionize-land-use-in-southeast-asia', null, '11'), ('3377', '251', '106', '2019', 'Association of South East Asian Nations Guidelines for Agroforestry Development public endorsement', 'The ASEAN Guidelines for Agroforestry Development developed by ICRAF is an important milestone towards increasing the prosperity, connectivity, resilience and security of the peoples of ASEAN Member States.', '', '1', '0.00', '4', '4', null, null, '0', '', null, '11'), ('3387', '252', '61', '2018', 'Draft Agroforestry Policy for Nepal at final stages of approval in government', null, '', '1', '0.00', '4', '3', null, null, null, 'ICRAF with the two ministries and support from the Climate Technology Centre and Network played a pivotal role in the formulation of the agroforestry policy. The Government of Nepal established an Inter-Ministerial Coordination Committee to oversee the policy’s development and nominated Javed Rizvi (ICRAF) as the only non-governmental member. The draft of the policy was finalized and handed over to Yogendra Kumar Karki, joint secretary and coordinator of the Inter-Ministerial Coordination Committee, by Rizvi in September 2018. ', null, '11');
INSERT INTO `project_policy_info` VALUES ('3390', '252', '106', '2019', 'Agroforestry Policy for Nepal ', 'An Agroforestry Policy for Nepal was launched in mid-2019 following extensive support to the government by ICRAF and partners since 2015. ', '', '1', '0.00', '4', '4', null, null, '0', '', null, '11'), ('3437', '256', '374', '2018', 'Guidelines for CGIAR Research Centers on the Nagoya Protocol on Access to Genetic Resources and the Fair and Equitable Sharing of Benefits Arising from their Utilization to the Convention on Biological Diversity', null, '', '1', '0.00', null, '4', null, null, null, '', null, '26'), ('3455', '258', '374', '2018', 'Updating the Standard Material Transfer Agreement under the Global Plan of Action (GPA) for Plant Genetic Resources for Food and Agriculture', null, '', '1', '0.00', null, '3', null, null, null, 'http://www.fao.org/3/CA1882EN/ca1882en.pdf\r\nAt its Seventh Session, the Governing Body requested the Ad Hoc Open-ended Working Group to Enhance the Functioning of the Multilateral System (Working Group) to revise the SMTA, based on the Report of the Working Group to the Seventh Session of the Governing Body and taking into account, inter alia, “further information or proposals that have been or may be submitted by Contracting Parties and stakeholder groups.”\r\nMoreover, the Governing Body ”appeal[ed] to stakeholders who use plant genetic resources under the International Treaty to assist the Working Group in finalizing the process for the enhancement of the Multilateral System, including by continuing to develop concrete proposals regarding the finalisation of the revised Standard Material Transfer Agreement, the process for further expansion of the scope of Annex I of the International Treaty, and ways to attract additional voluntary funding for the Benefit-sharing Fund on a sustainable basis, for consideration of the Working Group.”\r\nBased on these requests, the Secretary issued Notification NCP GB8-009, inviting Contracting Parties, stakeholder groups and stakeholders who use plant genetic resources under the International Treaty to make available inputs and proposals on the subjects being discussed by the Working Group at its eighth meeting.', null, '26'), ('3464', '259', '374', '2018', 'Green Pass Protocol to facilitate the exchange of phytosanitary clean germplasm from CGIAR centers.', null, '', '1', '0.00', null, '3', null, null, null, 'https://www.apaari.org/web/enhancing-germplasm-exchange/\r\n\r\nKey representatives from research institutions and organizations working in food and nutritional security across the globe met for two days in Rome from 18-19 April 2018 to share notes and ideas on how to develop phytosanitary protocols for expediting crop germplasm exchange with special reference to that of CGIAR gene banks.\r\n\r\nThe discussion led to development of a science based phytosanitary protocol (Green Pass) that takes into account the germplasm health at various stages of its collection, evaluation and conservation. The outputs of the meeting  would be submitted to FAO to make it as an international protocol or as a best practice for a faster and smoother exchange of germplasm that would eventually expedite the development of new varieties of crops that can meet food and nutritional security in the era of climate change.\r\n\r\nOrganized by Dr.  Lava Kumar, Head of Germplasm Health Unit at IITA, Nigeria, the other attendees-experts were; Dr. Ravi Khetarpal, Executive Secretary of APAARI in Thailand, Dr. Bonnie J. Furman Agric Officer at PGR-Agrobiodiversity FAO Rome, Dr. Charlotte Lusty Genebank Coordinator at Crop Trust, Germany, Dr. Radha Ranganatha from the International Seed Federation Switzerland and Dr.  Michael David Halewood from Bioversity International in Rome.\r\n', null, '26'), ('3473', '260', '191', '2019', 'Regional rice value chain upgrading strategy for West Africa', null, '', '1', '0.00', '1', '4', null, null, null, '', null, '16'), ('3474', '260', '192', '2019', 'Regional rice value chain upgrading strategy for West Africa', 'A regional rice value chain upgrading strategy was developed for West Africa, based on policy sequencing. The strategy is segmented based on geography and cultural heritage.', '', '1', '0.00', '1', '4', null, null, '1', '', null, '16'), ('3495', '262', '191', '2019', 'Vietnam policy supports for \"1M5R\" practice', null, '', '1', '0.00', '8', '5', null, null, null, '', null, '16'), ('3496', '262', '192', '2019', 'Vietnam policy supports for \"1M5R\" practice', 'One Must Do (certified seeds), Five Reductions (seed rate, fertilizer use, pesticide use, water use, postharvest losses)  or (1M5R) is Good Practice promoted through national programs like VNSat.  ', '', '1', '0.00', '8', '5', null, null, '0', '', null, '16'), ('3506', '263', '191', '2019', 'Institutionalizing certified sustainably-produced rice in Vietnam', null, '', '1', '0.00', '8', '3', null, null, null, '', null, '16'), ('3507', '263', '192', '2019', 'Institutionalizing certified sustainably-produced rice in Vietnam', null, '', '1', '0.00', '8', '3', null, null, null, null, null, '16'), ('3517', '264', '191', '2019', 'Philippine Law No. 10601, otherwise known as \"Agricultural and Fisheries Mechanization (AFMech) Law', null, '', '2', '0.00', '8', '4', null, null, null, '', null, '16'), ('3518', '264', '192', '2019', 'Philippine Law No. 10601, otherwise known as \"Agricultural and Fisheries Mechanization (AFMech) Law', 'Contribution to Philippine Law No. 10601, otherwise known as \"Agricultural and Fisheries Mechanization (AFMech) Law', '', '2', '0.00', '8', '4', null, null, '1', '', null, '16'), ('3528', '265', '191', '2019', 'Creating an enabling environment for crowding in private sector investment in sustainable production standards throughout rice value chains in Vietnam', null, '', '1', '0.00', '8', '3', null, null, null, 'IRRI. 2018. “Vietnam rice stakeholders discuss ways to increase mechanization and sustainability of rice 